import React, { useState, useEffect } from 'react';
import './App.css';

type AppStep = 'connect' | 'select' | 'mapping' | 'complete';
type ExcelFile = { id: string; name: string };
type ExcelColumn = { name: string };
type NotionProperty = { name: string; type?: string };
type Mapping = { excel_column: string; notion_property: string; data_type: string };

const API_BASE_URL = 'http://127.0.0.1:8000';

function App() {
  const [step, setStep] = useState<AppStep>('connect');
  const [excelFiles, setExcelFiles] = useState<ExcelFile[]>([]);
  const [excelColumns, setExcelColumns] = useState<ExcelColumn[]>([]);
  const [notionProperties, setNotionProperties] = useState<NotionProperty[]>([]);
  const [selectedExcelId, setSelectedExcelId] = useState<string>('');
  const [notionDbId, setNotionDbId] = useState<string>('');
  const [message, setMessage] = useState<string>('');
  const [userId, setUserId] = useState<string | null>(null);
  const [mappings, setMappings] = useState<Mapping[]>([]);
  const [unmatchedColumns, setUnmatchedColumns] = useState<string[]>([]);
  const [selectedColumnsToCreate, setSelectedColumnsToCreate] = useState<string[]>([]);
  const [syncDirection, setSyncDirection] = useState<'excel-to-notion' | 'notion-to-excel'>('excel-to-notion');

  // Microsoft ve Notion bağlantısı
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const authSuccess = params.get('auth_success');
    const receivedUserId = params.get('user_id');
    const storedUserId = localStorage.getItem('user_id');

    if (authSuccess === 'true' && receivedUserId) {
      setUserId(receivedUserId);
      setStep('select');
      localStorage.setItem('user_id', receivedUserId);
      fetchExcelFiles(receivedUserId);
      window.history.replaceState({}, document.title, window.location.pathname);
    } else if (storedUserId) {
      setUserId(storedUserId);
      setStep('select');
      fetchExcelFiles(storedUserId);
    } else setStep('connect');
  }, []);

  const handleMicrosoftConnect = () => {
    window.location.href = `${API_BASE_URL}/connect/microsoft`;
  };

  const fetchExcelFiles = async (currentUserId: string) => {
    try {
      const res = await fetch(`${API_BASE_URL}/get-excel-files?user_id=${currentUserId}`);
      const data = await res.json();
      if (res.ok) setExcelFiles(data.files);
      else setMessage(`Excel dosyaları alınamadı: ${data.error}`);
    } catch {
      setMessage('Excel dosyaları alınırken ağ hatası oluştu.');
    }
  };

  const fetchExcelColumns = async (fileId: string) => {
    if (!userId) return;
    try {
      const res = await fetch(`${API_BASE_URL}/get-excel-columns?excel_file_id=${fileId}&user_id=${userId}`);
      const data = await res.json();
      if (res.ok) {
        const columns = data.columns.map((name: string) => ({ name }));
        const hasId = columns.some((c: ExcelColumn) => c.name.toLowerCase() === 'id');
        if (!hasId) {
          setMessage('"ID" sütunu bulunmuyor, başka dosya seçin.');
          setExcelColumns([]);
          setMappings([]);
          return;
        }
        setExcelColumns(columns);
        setMappings(columns.map((c: ExcelColumn) => ({ excel_column: c.name, notion_property: '', data_type: '' })));
      } else setMessage(`Excel sütunları alınamadı: ${data.error}`);
    } catch {
      setMessage('Excel sütunları alınırken ağ hatası oluştu.');
    }
  };

  const fetchNotionProperties = async () => {
    if (!notionDbId) {
      setMessage('Lütfen Notion Veritabanı ID\'si girin.');
      return;
    }
    try {
      const res = await fetch(`${API_BASE_URL}/get-notion-properties?notion_database_id=${notionDbId}`);
      const data = await res.json();
      if (res.ok) {
        setNotionProperties(data.properties);
        const notionPropNames = data.properties.map((p: NotionProperty) => p.name.toLowerCase());
        const unmatched = excelColumns
          .filter(col => !notionPropNames.includes(col.name.toLowerCase()) && col.name.toLowerCase() !== 'id')
          .map(col => col.name);
        setUnmatchedColumns(unmatched);
        setSelectedColumnsToCreate(unmatched);
        setStep('mapping');
      } else setMessage(`Notion özellikleri alınamadı: ${data.error}`);
    } catch {
      setMessage('Notion özelliklerini alırken ağ hatası oluştu.');
    }
  };

  const handleMappingChange = (excelCol: string, notionProp: string) => {
    setMappings(prev => prev.map(m =>
      (syncDirection === 'excel-to-notion' ? m.excel_column : m.notion_property) === (syncDirection === 'excel-to-notion' ? excelCol : notionProp)
        ? {
            ...m,
            excel_column: syncDirection === 'excel-to-notion' ? m.excel_column : excelCol,
            notion_property: syncDirection === 'excel-to-notion' ? notionProp : m.notion_property,
            data_type: notionProperties.find(p => p.name === notionProp)?.type || ''
          } : m
    ));
  };

  const handleCreateColumnChange = (colName: string, checked: boolean) => {
    setSelectedColumnsToCreate(prev => checked ? [...prev, colName] : prev.filter(c => c !== colName));
  };

  const saveMappingAndStartSync = async () => {
    if (!userId) { setMessage('Kullanıcı kimliği bulunamadı.'); return; }
    const validMappings = mappings.filter(m => m.notion_property.trim() !== '');
    if (validMappings.length === 0) { setMessage("En az bir sütunu eşleştirin."); return; }

    try {
      const res = await fetch(`${API_BASE_URL}/sync/${syncDirection}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          excel_file_id: selectedExcelId,
          notion_database_id: notionDbId,
          user_id: parseInt(userId, 10),
          mappings: validMappings,
          columns_to_create: selectedColumnsToCreate
        }),
      });
      if (res.ok) setStep('complete');
      else { const err = await res.json(); setMessage(`Senkronizasyon başarısız: ${err.error || err.detail}`); }
    } catch {
      setMessage('Senkronizasyon sırasında ağ hatası oluştu.');
    }
  };

  // ---------- Render Functions ----------
  const renderConnectStep = () => (
    <div className="step-container">
      <button className="primary-btn" onClick={handleMicrosoftConnect}>Microsoft Hesabını Bağla</button>
    </div>
  );

  const renderSelectStep = () => (
    <div className="step-container grid">
      <div className="field">
        <label>Excel Dosyası Seçin:</label>
        <select value={selectedExcelId} onChange={e => { setSelectedExcelId(e.target.value); fetchExcelColumns(e.target.value); }}>
          <option value="">Dosya Seçiniz...</option>
          {excelFiles.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
        </select>
      </div>
      <div className="field">
        <label>Notion Veritabanı ID'si:</label>
        <input type="text" value={notionDbId} onChange={e => setNotionDbId(e.target.value)} placeholder="Veritabanı ID'sini yapıştırın" />
      </div>
      <button className="primary-btn" onClick={fetchNotionProperties} disabled={!selectedExcelId || !notionDbId}>Notion Özelliklerini Getir</button>
    </div>
  );

  const renderMappingStep = () => (
    <div className="step-container">
      <h2>Sütunları Eşleştirin</h2>

      {/* Toggle Direction */}
      <div className="toggle-direction">
        <button
          className={syncDirection === 'excel-to-notion' ? 'active' : ''}
          onClick={() => setSyncDirection('excel-to-notion')}>Excel → Notion</button>
        <button
          className={syncDirection === 'notion-to-excel' ? 'active' : ''}
          onClick={() => setSyncDirection('notion-to-excel')}>Notion → Excel</button>
      </div>

      <div className="mapping-grid">
        <div className="mapping-column">
          <h3>{syncDirection === 'excel-to-notion' ? 'Excel Sütunları' : 'Notion Özellikleri'}</h3>
          {(syncDirection === 'excel-to-notion' ? excelColumns : notionProperties).map(c => (
            <div key={c.name} className="mapping-item">{c.name}</div>
          ))}
        </div>
        <div className="mapping-column">
          <h3>{syncDirection === 'excel-to-notion' ? 'Notion Özellikleri' : 'Excel Sütunları'}</h3>
          {mappings.map(m => (
            <select
              key={syncDirection === 'excel-to-notion' ? m.excel_column : m.notion_property}
              value={syncDirection === 'excel-to-notion' ? m.notion_property : m.excel_column}
              onChange={e => handleMappingChange(syncDirection === 'excel-to-notion' ? m.excel_column : m.notion_property, e.target.value)}
            >
              <option value="">Seçiniz...</option>
              {(syncDirection === 'excel-to-notion' ? notionProperties : excelColumns).map(p =>
                <option key={p.name} value={p.name}>{p.name}{'type' in p && p.type ? ` (${p.type})` : ''}</option>
              )}
            </select>
          ))}
        </div>
      </div>

      {syncDirection === 'excel-to-notion' && unmatchedColumns.length > 0 && (
        <div className="unmatched-columns">
          <h3>Notion'da Olmayan Sütunlar</h3>
          {unmatchedColumns.map(c => (
            <label key={c}>
              <input type="checkbox" checked={selectedColumnsToCreate.includes(c)} onChange={e => handleCreateColumnChange(c, e.target.checked)} />
              {c}
            </label>
          ))}
        </div>
      )}

      <div className="sync-buttons">
        <button className="primary-btn" onClick={saveMappingAndStartSync}>Sync Başlat</button>
      </div>
    </div>
  );

  const renderCompleteStep = () => (
    <div className="step-container">
      <h2>Senkr. Tamamlandı!</h2>
      <button className="primary-btn" onClick={() => setStep('select')}>Yeni Senkronizasyon</button>
    </div>
  );

  return (
    <div className="App">
      <header>
        <h1>Excel ↔ Notion Senkronizasyonu</h1>
        {message && <div className="message">{message}</div>}
      </header>
      <main>
        {step === 'connect' && renderConnectStep()}
        {step === 'select' && renderSelectStep()}
        {step === 'mapping' && renderMappingStep()}
        {step === 'complete' && renderCompleteStep()}
      </main>
    </div>
  );
}

export default App;
