import React, { useState } from 'react';
import './App.css';

const USER_ID = 1; 

function App() {
  const [step, setStep] = useState('connect'); 
  const [excelFiles, setExcelFiles] = useState<{ id: string; name: string }[]>([]);
  const [selectedExcelId, setSelectedExcelId] = useState<string>('');
  const [notionDbId, setNotionDbId] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const API_BASE_URL = 'http://127.0.0.1:8000';

  const handleMicrosoftConnect = () => {
    window.location.href = `${API_BASE_URL}/connect/microsoft`;
  };

  const handleNotionConnect = () => {
    setMessage('Notion hesabınızı bağlamak için, Notion\'da herhangi bir veritabanı veya sayfasına gidin, "..." menüsünden "Add connections"ı seçin ve oluşturduğunuz entegrasyonu ("Excel Sync") ekleyin.');
  };

  const fetchExcelFiles = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/get-excel-files?user_id=${USER_ID}`);
      if (!response.ok) {
        throw new Error('Dosyalar alınamadı.');
      }
      const data = await response.json();
      setExcelFiles(data.files);
      setStep('select');
    } catch (error) {
      if (error instanceof Error) {
        setMessage(`Hata: ${error.message}. Lütfen backend sunucunuzun çalıştığından ve kullanıcı kimliğinizin geçerli olduğundan emin olun.`);
      } else {
        setMessage('Bilinmeyen bir hata oluştu.');
      }
    }
  };

  const handleGoToMapping = () => {
    if (selectedExcelId && notionDbId) {
      setMessage('Şimdi eşleme arayüzüne geçebilirsiniz.');
      // Burada /mapping sayfasına yönlendirme veya state değişikliği yapılabilir.
    } else {
      setMessage('Lütfen bir Excel dosyası ve Notion veritabanı kimliği seçin.');
    }
  };

  const renderConnectStep = () => (
    <div>
      <p>Başlamak için hesaplarınızı bağlayın.</p>
      <button onClick={handleMicrosoftConnect}>Microsoft Hesabını Bağla</button>
      <button onClick={handleNotionConnect}>Notion Hesabını Bağla</button>
      <p>Microsoft bağlantısını tamamladıktan sonra sayfayı yenileyin ve dosyaları listelemek için aşağıdaki butona tıklayın.</p>
      <button onClick={fetchExcelFiles}>Dosyaları Listele</button>
    </div>
  );

  const renderSelectStep = () => (
    <div>
      <h2>Excel Dosyanızı ve Notion Veritabanınızı Seçin</h2>

      <div className="file-selection-container">
        <div className="excel-files">
          <h3>Excel Dosyaları</h3>
          {excelFiles.length > 0 ? (
            <ul>
              {excelFiles.map((file) => (
                <li key={file.id}>
                  <label>
                    <input
                      type="radio"
                      name="excelFile"
                      value={file.id}
                      checked={selectedExcelId === file.id}
                      onChange={(e) => setSelectedExcelId(e.target.value)}
                    />
                    {file.name}
                  </label>
                </li>
              ))}
            </ul>
          ) : (
            <p>Excel dosyası bulunamadı. Lütfen OneDrive hesabınızda .xlsx dosyası olduğundan emin olun.</p>
          )}
        </div>

        <div className="notion-db">
          <h3>Notion Veritabanı ID</h3>
          <p>Notion veritabanı ID'sini URL'den kopyalayıp yapıştırın.</p>
          <input
            type="text"
            placeholder="Notion Veritabanı ID'sini girin..."
            value={notionDbId}
            onChange={(e) => setNotionDbId(e.target.value)}
          />
        </div>
      </div>

      <button onClick={handleGoToMapping}>Eşlemeye Geç</button>
    </div>
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>Notion & Excel Senkronizasyonu</h1>

        {message && <p className="message">{message}</p>}

        {step === 'connect' ? renderConnectStep() : renderSelectStep()}
      </header>
    </div>
  );
}

export default App;
