import React, { useEffect, useState } from "react";
import axios from "axios";

type AutoSyncRecord = {
  id: number;
  excel_file_id: string;
  excel_file_name: string;
  notion_database_id: string;
  notion_database_name?: string;
  direction: "excel-to-notion" | "notion-to-excel";
  columns: string[];
};

export default function AutoSyncDashboard() {
  const [records, setRecords] = useState<AutoSyncRecord[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const userId = localStorage.getItem("user_id"); // App.tsx’te zaten kaydediyorsun
  const backendBase = "http://127.0.0.1:8000";

  // Listeyi çek
  const fetchRecords = async () => {
    if (!userId) return;
    setLoading(true);
    try {
      const res = await axios.get(`${backendBase}/list-auto-sync`, {
        params: { user_id: userId },
      });
      setRecords(res.data.records);
      setError(null);
    } catch (err) {
      console.error("Liste alınamadı:", err);
      setError("Otomatik sync listesi alınamadı.");
    } finally {
      setLoading(false);
    }
  };

  // Tek sütunu kaldır
  const removeColumn = async (recordId: number, column: string) => {
    try {
      await axios.post(`${backendBase}/remove-auto-sync-column`, {
        record_id: recordId,
        column,
      });
      fetchRecords();
    } catch (err) {
      console.error("Sütun silinemedi:", err);
      alert("Sütun silinemedi.");
    }
  };

  // Kaydı tamamen sil
  const deleteRecord = async (recordId: number) => {
    try {
      await axios.post(`${backendBase}/delete-auto-sync`, { record_id: recordId });
      fetchRecords();
    } catch (err) {
      console.error("Kayıt silinemedi:", err);
      alert("Kayıt silinemedi.");
    }
  };

  useEffect(() => {
    fetchRecords();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Otomatik Sync Dashboard</h1>

      {loading && <p>Yükleniyor...</p>}
      {error && <p className="text-red-500">{error}</p>}

      {records.length === 0 && !loading && <p>Henüz otomatik sync ayarı yok.</p>}

      <div className="space-y-4">
        {records.map((rec) => (
          <div
            key={rec.id}
            className="border rounded p-4 shadow-sm bg-gray-50"
          >
            <div className="flex justify-between items-center mb-2">
              <h2 className="font-semibold">
                {rec.excel_file_name} → {rec.notion_database_name || rec.notion_database_id}{" "}
                <span className="text-sm text-gray-500">
                  ({rec.direction})
                </span>
              </h2>
              <button
                onClick={() => deleteRecord(rec.id)}
                className="bg-red-500 text-white px-3 py-1 rounded"
              >
                Kaydı Sil
              </button>
            </div>

            {rec.columns.length > 0 ? (
              <ul className="list-disc pl-5">
                {rec.columns.map((col) => (
                  <li key={col} className="flex justify-between items-center">
                    <span>{col}</span>
                    <button
                      onClick={() => removeColumn(rec.id, col)}
                      className="text-xs bg-gray-300 px-2 py-1 rounded"
                    >
                      Kaldır
                    </button>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-sm text-gray-500">Hiç otomatik sync sütunu yok.</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
