// Home.tsx
import React from 'react';

// Projenizin backend'inde tanÄ±mladÄ±ÄŸÄ±nÄ±z Microsoft baÄŸlantÄ± URL'si
const MICROSOFT_CONNECT_URL = 'http://127.0.0.1:8000/connect/microsoft'; 

// ðŸš¨ KRÄ°TÄ°K: setStep prop'u eklenmeli
interface HomeProps {
    setStep: (step: 'home' | 'privacy' | 'terms' | 'connect' | 'dashboard' | 'profile' | 'pricing') => void;
}

/**
 * Ana sayfa iÃ§eriÄŸini (global header ve footer hariÃ§) render eden bileÅŸendir.
 */
const Home: React.FC<HomeProps> = ({ setStep }) => { // setStep'i prop olarak alÄ±yoruz
    return (
        <div className="home-page-container">
            {/* --- ANA Ä°Ã‡ERÄ°K: HERO BÃ–LÃœMÃœ --- */}
            <section className="hero-section">
                <div className="container hero-content">
                    <div className="hero-text">
                        {/* Ä°ngilizce baÅŸlÄ±k ve alt metinler korunuyor */}
                        <h1>Stop Wasting Time on Manual Notion and Excel Syncs.</h1>
                        <p className="subtitle">Automate your data flow, ensure accuracy across your entire team, and save hours every week. Setup takes less than 60 seconds.</p>
                        
                        <div className="hero-cta-group">
                            {/* Ana CTA: Log in / Get Started */}
                            <a href={MICROSOFT_CONNECT_URL} className="btn btn-primary cta-main">
                                Get Started - It's Free to Try
                            </a>
                            
                            {/* Ä°kincil CTA: Pricing sayfasÄ±na yÃ¶nlendirme (setStep kullanÄ±larak) */}
                            <button 
                                onClick={() => setStep('pricing')} 
                                className="btn btn-secondary cta-pricing"
                            >
                                View Pricing Plans
                            </button>
                        </div>
                    </div>
                    <div className="hero-image">
                        {/* GÃ¶z alÄ±cÄ± bir gÃ¶rsel eklentisi (Ã–rn: Notion ve Excel logolarÄ± arasÄ±nda veri akÄ±ÅŸÄ±nÄ± gÃ¶steren bir gÃ¶rsel) */}
                        <img src="/assets/sync-visual.svg" alt="Notion and Excel Synchronization Visual" />
                    </div>
                </div>
            </section>

            {/* --- Ã–ZELLÄ°KLER BÃ–LÃœMÃœ --- */}
            <section id="features" className="features-section">
                <div className="container">
                    <h2>Features That Simplify Your Data Workflows</h2>
                    <div className="feature-grid">
                        <div className="feature-card">
                            <h3>Real-time Bi-directional Sync</h3>
                            <p>Keep your Notion database and Excel spreadsheet perfectly matched, automatically pushing changes both ways.</p>
                        </div>
                        <div className="feature-card">
                            <h3>Granular Control & Mapping</h3>
                            <p>Easily map specific Excel columns to Notion properties without writing a single line of code.</p>
                        </div>
                        <div className="feature-card">
                            <h3>Set & Forget Automation</h3>
                            <p>Configure automatic synchronization jobs to run every 15, 30, or 60 minutes based on your plan.</p>
                        </div>
                    </div>
                </div>
            </section>
            
            {/* Daha fazla bÃ¶lÃ¼m (FAQ, Testimonials) buraya eklenebilir */}
        </div>
    );
};

export default Home;
