import React, { useEffect, useState } from "react";

type AutoSync = {
  id: number;
  excel_file_id: string;
  notion_database_id: string;
  direction: string;
  excel_column: string;
  notion_property: string;
  data_type: string;
  created_at: string;
};

const AutoSyncDashboard: React.FC = () => {
  const [autoSyncs, setAutoSyncs] = useState<AutoSync[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAutoSyncs = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/list-auto-sync?user_id=1"
        );
        const data = await response.json();
        console.log("DEBUG gelen auto-sync data:", data);
        setAutoSyncs(data.auto_syncs || []);
      } catch (error) {
        console.error("Auto sync fetch error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAutoSyncs();
  }, []);

  const handleDisable = async (id: number) => {
    console.log("Otomatik sync kapatılacak ID:", id);
    // TODO: backend'e disable endpoint ekleyip burada çağıracağız
  };

  if (loading) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Otomatik Sync Dashboard</h2>

      {autoSyncs.length === 0 ? (
        <p>Henüz otomatik sync tanımlı değil.</p>
      ) : (
        autoSyncs.map((sync) => (
          <div
            key={sync.id}
            className="p-4 border rounded mb-3 shadow-sm bg-white"
          >
            <p className="font-semibold">
              {sync.excel_file_id} → {sync.notion_database_id}
            </p>
            <p>Yön: {sync.direction}</p>
            <p>
              Excel Sütunu: <b>{sync.excel_column}</b>
            </p>
            <p>
              Notion Property: <b>{sync.notion_property}</b>
            </p>
            <button
              onClick={() => handleDisable(sync.id)}
              className="mt-2 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            >
              Otomatik Sync Kapat
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default AutoSyncDashboard;
