import React, { useState, useEffect } from 'react';
import './App.css';

type AppStep = 'connect' | 'select' | 'mapping' | 'complete';
type ExcelFile = { id: string; name: string };
type ExcelColumn = { name: string };
type NotionProperty = { name: string; type: string };
type Mapping = { excel_column: string; notion_property: string; data_type: string };

const API_BASE_URL = 'http://127.0.0.1:8000';

function App() {
  const [step, setStep] = useState<AppStep>('connect');
  const [excelFiles, setExcelFiles] = useState<ExcelFile[]>([]);
  const [excelColumns, setExcelColumns] = useState<ExcelColumn[]>([]);
  const [notionProperties, setNotionProperties] = useState<NotionProperty[]>([]);
  const [selectedExcelId, setSelectedExcelId] = useState<string>('');
  const [notionDbId, setNotionDbId] = useState<string>('');
  const [message, setMessage] = useState<string>('');
  const [userId, setUserId] = useState<string | null>(null);
  const [mappings, setMappings] = useState<Mapping[]>([]);
  const [unmatchedColumns, setUnmatchedColumns] = useState<string[]>([]);
  const [selectedColumnsToCreate, setSelectedColumnsToCreate] = useState<string[]>([]);

  const allExcelColumnNames = excelColumns.map(col => col.name);
  const allNotionPropertyNames = notionProperties.map(prop => prop.name);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const authSuccess = params.get('auth_success');
    const authError = params.get('auth_error');
    const receivedUserId = params.get('user_id');
    const storedUserId = localStorage.getItem('user_id');

    if (authSuccess === 'true' && receivedUserId) {
      setUserId(receivedUserId);
      setStep('select');
      localStorage.setItem('user_id', receivedUserId);
      fetchExcelFiles(receivedUserId);
      window.history.replaceState({}, document.title, window.location.pathname);
    } else if (authError === 'true') {
      setMessage(`Yetkilendirme hatası: ${params.get('message')}`);
      setStep('connect');
    } else if (storedUserId) {
      setUserId(storedUserId);
      setStep('select');
      fetchExcelFiles(storedUserId);
    } else {
      setStep('connect');
    }
  }, []);

  const handleMicrosoftConnect = () => {
    window.location.href = `${API_BASE_URL}/connect/microsoft`;
  };

  const handleNotionConnect = () => {
    setMessage(
      'Notion hesabınızı bağlamak için, Notion\'da herhangi bir veritabanı veya sayfasına gidin, "..." menüsünden "Add connections"ı seçin ve oluşturduğunuz entegrasyonu ("Excel Sync") ekleyin.'
    );
  };

  const fetchExcelFiles = async (currentUserId: string) => {
    try {
      const response = await fetch(`${API_BASE_URL}/get-excel-files?user_id=${currentUserId}`);
      const data = await response.json();
      if (response.ok) {
        setExcelFiles(data.files);
      } else {
        setMessage(`Excel dosyaları alınamadı: ${data.error}`);
      }
    } catch (error) {
      setMessage('Excel dosyaları alınırken ağ hatası oluştu.');
    }
  };

  const fetchExcelColumns = async (fileId: string) => {
    if (!userId) return;
    try {
      const response = await fetch(`${API_BASE_URL}/get-excel-columns?excel_file_id=${fileId}&user_id=${userId}`);
      const data = await response.json();
      if (response.ok) {
        const columns = data.columns.map((name: string) => ({ name }));

        const hasIdColumn = columns.some((col: any) => col.name.toLowerCase() === 'id');
        if (!hasIdColumn) {
          setMessage(
            'Seçtiğiniz Excel dosyasında "ID" adında bir sütun bulunmuyor. Senkronizasyon için bir "ID" sütunu gereklidir.'
          );
          setExcelColumns([]);
          setMappings([]);
          return;
        }

        setExcelColumns(columns);
        setMappings(
          data.columns.map((name: string) => ({
            excel_column: name,
            notion_property: '',
            data_type: '',
          }))
        );
        setMessage('');
      } else {
        setMessage(`Excel sütunları alınamadı: ${data.error}`);
      }
    } catch (error) {
      setMessage('Excel sütunları alınırken ağ hatası oluştu.');
    }
  };

  const fetchNotionProperties = async () => {
    if (!notionDbId) {
      setMessage('Lütfen bir Notion Veritabanı ID\'si girin.');
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/get-notion-properties?notion_database_id=${notionDbId}`);
      const data = await response.json();
      if (response.ok) {
        setNotionProperties(data.properties);

        const notionPropNames = data.properties.map((p: any) => p.name.toLowerCase());
        const unmatched = excelColumns
          .filter(
            col => !notionPropNames.includes(col.name.toLowerCase()) && col.name.toLowerCase() !== 'id'
          )
          .map(col => col.name);

        setUnmatchedColumns(unmatched);
        setSelectedColumnsToCreate(unmatched);

        setStep('mapping');
        setMessage('');
      } else {
        setMessage(`Notion özellikleri alınamadı: ${data.error}`);
      }
    } catch (error) {
      setMessage('Notion özelliklerini alırken ağ hatası oluştu.');
    }
  };

  const handleExcelFileChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const fileId = e.target.value;
    setSelectedExcelId(fileId);
    if (fileId) {
      fetchExcelColumns(fileId);
    }
  };

  const handleMappingChange = (excelColumnName: string, notionPropertyName: string) => {
    setMappings(prevMappings => {
      const newMappings = [...prevMappings];
      const mappingIndex = newMappings.findIndex(m => m.excel_column === excelColumnName);

      if (mappingIndex > -1) {
        const selectedNotionProp = notionProperties.find(p => p.name === notionPropertyName);
        newMappings[mappingIndex] = {
          ...newMappings[mappingIndex],
          notion_property: notionPropertyName,
          data_type: selectedNotionProp?.type || '',
        };
      }
      return newMappings;
    });
  };

  const handleCreateColumnChange = (columnName: string, isChecked: boolean) => {
    setSelectedColumnsToCreate(prevSelected =>
      isChecked ? [...prevSelected, columnName] : prevSelected.filter(name => name !== columnName)
    );
  };

  const saveMappingAndStartSync = async (syncDirection: 'excel-to-notion' | 'notion-to-excel') => {
    if (!userId) {
      setMessage('Kullanıcı kimliği bulunamadı. Lütfen tekrar giriş yapın.');
      return;
    }

    const validMappings = mappings.filter(m => m.notion_property && m.notion_property.trim() !== '');
    if (validMappings.length === 0) {
      setMessage('Lütfen en az bir sütunu Notion özelliğiyle eşleştirin.');
      return;
    }

    try {
      const syncResponse = await fetch(`${API_BASE_URL}/sync/${syncDirection}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          excel_file_id: selectedExcelId,
          notion_database_id: notionDbId,
          user_id: parseInt(userId, 10),
          mappings: validMappings,
          columns_to_create: selectedColumnsToCreate,
        }),
      });

      if (!syncResponse.ok) {
        const syncError = await syncResponse.json();
        setMessage(`Senkronizasyon başlatılamadı: ${syncError.detail || syncError.error}`);
        return;
      }

      setStep('complete');
      setMessage('Senkronizasyon başarıyla tamamlandı!');
    } catch (error) {
      setMessage('Senkronizasyon başlatma sırasında ağ hatası oluştu.');
    }
  };

  const renderConnectStep = () => (
    <div className="step-container">
      <button onClick={handleMicrosoftConnect}>Microsoft Hesabını Bağla</button>
    </div>
  );

  const renderSelectStep = () => (
    <div className="step-container">
      <h2>Excel Dosyasını ve Notion Veritabanını Seçin</h2>
      <div>
        <label>
          Excel Dosyası Seçin:
          <select value={selectedExcelId} onChange={handleExcelFileChange}>
            <option value="">Dosya Seçiniz...</option>
            {excelFiles.map(file => (
              <option key={file.id} value={file.id}>
                {file.name}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Notion Veritabanı ID'si:
          <input
            type="text"
            value={notionDbId}
            onChange={e => setNotionDbId(e.target.value)}
            placeholder="Veritabanı ID'sini yapıştırın"
          />
        </label>
      </div>
      <button onClick={fetchNotionProperties} disabled={!selectedExcelId || !notionDbId}>
        Notion Özelliklerini Getir
      </button>
    </div>
  );

  const renderMappingStep = () => (
    <div className="step-container">
      <h2>Sütunları Eşleştirin</h2>
      <div className="mapping-grid">
        <div className="mapping-section">
          <h3>Excel Sütunları</h3>
          <ul>
            {excelColumns.map((column, index) => (
              <li key={index}>
                <span className="excel-name">{column.name}</span>
              </li>
            ))}
          </ul>
        </div>
        <div className="mapping-section">
          <h3>Notion Özellikleri</h3>
          <ul>
            {mappings.map((mapping, index) => (
              <li key={index}>
                <select
                  value={mapping.notion_property}
                  onChange={e => handleMappingChange(mapping.excel_column, e.target.value)}
                >
                  <option value="">Seçiniz...</option>
                  {notionProperties.map(prop => (
                    <option key={prop.name} value={prop.name}>
                      {prop.name} ({prop.type})
                    </option>
                  ))}
                </select>
              </li>
            ))}
          </ul>
        </div>
      </div>
      {unmatchedColumns.length > 0 && (
        <div className="unmatched-columns-container">
          <h3>Notion'da Olmayan Sütunlar</h3>
          <p>
            Aşağıdaki Excel sütunları Notion veritabanınızda bulunamadı. Notion'da otomatik olarak oluşturmak
            istediklerinizi seçin.
          </p>
          <ul>
            {unmatchedColumns.map(colName => (
              <li key={colName}>
                <label>
                  <input
                    type="checkbox"
                    checked={selectedColumnsToCreate.includes(colName)}
                    onChange={e => handleCreateColumnChange(colName, e.target.checked)}
                  />
                  {colName}
                </label>
              </li>
            ))}
          </ul>
        </div>
      )}
      <div className="sync-buttons-container">
        <button onClick={() => saveMappingAndStartSync('excel-to-notion')} disabled={!mappings.length}>
          Excel'den Notion'a Eşitle
        </button>
        <button onClick={() => saveMappingAndStartSync('notion-to-excel')} disabled={!mappings.length}>
          Notion'dan Excel'e Eşitle
        </button>
      </div>
    </div>
  );

  const renderCompleteStep = () => (
    <div className="step-container">
      <h2>Senkronizasyon Başarılı!</h2>
      <p>
        Seçtiğiniz yönde senkronizasyon tamamlandı. Uygulama, dosyanızdaki değişiklikleri takip etmeye ve verileri
        otomatik olarak senkronize etmeye devam edecektir.
      </p>
      <button onClick={() => setStep('select')}>Yeni Senkronizasyon Başlat</button>
    </div>
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>Notion & Excel Senkronizasyonu</h1>

        {message && <p className="message">{message}</p>}

        {step === 'connect' && renderConnectStep()}
        {step === 'select' && renderSelectStep()}
        {step === 'mapping' && renderMappingStep()}
        {step === 'complete' && renderCompleteStep()}
      </header>
    </div>
  );
}

export default App;
