// AutoSyncDashboard.tsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ChevronDown, ChevronUp, XSquare, ToggleRight, ToggleLeft } from 'lucide-react';

// --- TİP TANIMLARI (Backend'deki AutoSyncJob modeline uygun) ---

// Sütun Eşleşme Detayları
type MappingDetail = {
  id: number;
  excel_column: string;
  notion_property: string;
  is_auto_sync_enabled: boolean; // Bireysel sütun sync durumu
};

// Otomatik Sync İş Detayları
type AutoSyncJob = {
  id: number;
  direction: 'excel-to-notion' | 'notion-to-excel'; 
  excel_file_name: string; // Backend'den çekilen dosya/sayfa adı
  notion_db_name: string;   // Backend'den çekilen dosya/sayfa adı
  is_active: boolean;      // Tüm işin aktiflik durumu
  mapping_details: MappingDetail[]; // Sütun eşleşmelerinin listesi
};

const API_BASE_URL = 'http://127.0.0.1:8000'; 
// Kullanıcının oturum açmış ID'sini localStorage'dan veya varsayılan değerden çekin.
const USER_ID = localStorage.getItem('user_id') || '1'; 

const AutoSyncDashboard: React.FC = () => {
  const [jobs, setJobs] = useState<AutoSyncJob[]>([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState<string>('');
  // Hangi işin detaylarının açık olduğunu tutmak için state
  const [expandedJobId, setExpandedJobId] = useState<number | null>(null); 

  // --- API İŞLEMLERİ ---

  const fetchJobs = async () => {
    setLoading(true);
    setMessage('');
    try {
      // Backend'deki /autosync/list/{user_id} endpoint'ini çağırıyoruz
      const response = await axios.get<AutoSyncJob[]>(`${API_BASE_URL}/autosync/list/${USER_ID}`);
      setJobs(response.data);
    } catch (error) {
      const msg = axios.isAxiosError(error) && error.response 
        ? error.response.data.detail || 'Bilinmeyen hata' 
        : 'Auto sync yapılandırmalarını alırken ağ hatası oluştu.';
      setMessage(`Sync işleri çekilemedi: ${msg}`);
      console.error("Auto sync fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, [USER_ID]);

  // Yeni İşlem: Bireysel Sütun Sync'ini Aç/Kapat
  const toggleMappingSync = async (mappingId: number) => {
    setMessage('');
    try {
      // Backend'deki /autosync/toggle-mapping/{mapping_id} endpoint'ini çağırıyoruz
      await axios.put(`${API_BASE_URL}/autosync/toggle-mapping/${mappingId}`);
      setMessage("Sütun senkronizasyon durumu başarıyla güncellendi.");
      fetchJobs(); // Başarılı olursa verileri yeniden çek
    } catch (error) {
      setMessage("Sütun durumu güncellenemedi.");
      console.error("Sütun durumu güncellenemedi:", error);
    }
  };

  // İşlem: Tüm İşi Sil (Kapat)
  const deleteSyncJob = async (jobId: number, jobName: string) => {
    setMessage('');
    if (!window.confirm(`"${jobName}" adlı otomatik senkronizasyon işini ve zamanlayıcısını tamamen silmek istediğinizden emin misiniz?`)) {
      return;
    }
    try {
      // Backend'deki /autosync/delete/{job_id} endpoint'ini çağırıyoruz
      await axios.delete(`${API_BASE_URL}/autosync/delete/${jobId}`);
      setMessage("Otomatik Sync işi başarıyla durduruldu ve silindi.");
      setExpandedJobId(null);
      fetchJobs(); // Başarılı olursa listeyi yeniden çek
    } catch (error) {
      setMessage("Sync işi silinemedi.");
      console.error("Sync işi silinemedi:", error);
    }
  };

  // UI İçin Başlık Oluşturucu
  const renderJobTitle = (job: AutoSyncJob) => {
    const directionArrow = job.direction === 'excel-to-notion' ? '→' : '←';
    const source = job.direction === 'excel-to-notion' ? job.excel_file_name : job.notion_db_name;
    const target = job.direction === 'excel-to-notion' ? job.notion_db_name : job.excel_file_name;

    return `${source} ${directionArrow} ${target}`;
  };


  if (loading) {
    return <p className="p-4">Yükleniyor...</p>;
  }

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Otomatik Senkronizasyon Yönetimi</h1>
      
      {message && <div className="p-3 mb-4 rounded bg-blue-100 text-blue-800">{message}</div>}

      {jobs.length === 0 ? (
        <p className="text-gray-500">Aktif otomatik senkronizasyon işi bulunmamaktadır.</p>
      ) : (
        <div className="space-y-4">
          {jobs.map((job) => (
            <div 
              key={job.id} 
              className="border border-gray-200 rounded-lg shadow-md overflow-hidden"
            >
              {/* ANA BAŞLIK ALANI (Tıklanabilir) */}
              <div 
                className="flex justify-between items-center p-4 bg-white cursor-pointer hover:bg-gray-50 transition duration-150"
                onClick={() => setExpandedJobId(job.id === expandedJobId ? null : job.id)}
              >
                <span className="text-lg font-medium">
                  {renderJobTitle(job)}
                </span>
                <div className="flex items-center space-x-4">
                    {/* Tüm İşi Sil Butonu */}
                    <button 
                        onClick={(e) => { e.stopPropagation(); deleteSyncJob(job.id, renderJobTitle(job)); }}
                        className="p-1 rounded-full text-red-600 hover:bg-red-100"
                        title="Tüm Sync İşini Sil"
                    >
                        <XSquare size={20} />
                    </button>
                    {/* Genişlet/Daralt İkonu */}
                    {job.id === expandedJobId ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
                </div>
              </div>
              
              {/* DETAY ALANI (Genişletilmiş İçerik) */}
              {job.id === expandedJobId && (
                <div className="p-4 bg-gray-50 border-t border-gray-200">
                  <h3 className="font-semibold mb-3 text-sm text-gray-700">
                    <span className="font-bold">AKTİF EŞLEŞMELER:</span> {job.mapping_details.filter(m => m.is_auto_sync_enabled).length} / {job.mapping_details.length}
                  </h3>
                  <div className="space-y-2">
                    {job.mapping_details.map((mapping) => (
                      <div 
                        key={mapping.id} 
                        className={`flex justify-between items-center p-3 rounded-md border transition duration-150 ${mapping.is_auto_sync_enabled ? 'bg-white border-green-300' : 'bg-gray-200 border-gray-300'}`}
                      >
                        {/* Eşleşme İsimleri */}
                        <span className="text-sm">
                          **{mapping.excel_column}** <span className="text-gray-500 mx-2">⇆</span> 
                          **{mapping.notion_property}**
                        </span>
                        
                        {/* Sync Toggle Butonu */}
                        <button 
                          onClick={() => toggleMappingSync(mapping.id)}
                          className={`p-1 rounded-full transition duration-150 ${mapping.is_auto_sync_enabled ? 'text-green-600 hover:bg-green-100' : 'text-gray-500 hover:bg-gray-300'}`}
                          title={mapping.is_auto_sync_enabled ? "Otomatik Sync Açık (Kapatmak için tıkla)" : "Otomatik Sync Kapalı (Açmak için tıkla)"}
                        >
                          {mapping.is_auto_sync_enabled ? <ToggleRight size={24} /> : <ToggleLeft size={24} />}
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AutoSyncDashboard;
