import React, { useState } from "react";

function MappingSection({ excelColumns, notionProperties, onChange }) {
  const [selectedExcel, setSelectedExcel] = useState("");
  const [selectedNotion, setSelectedNotion] = useState("");
  const [mappings, setMappings] = useState([]);

  const addMapping = () => {
    if (!selectedExcel || !selectedNotion) return;

    // Aynı Excel sütunu ikinci kez eklenmesin
    if (mappings.some((m) => m.excel_column === selectedExcel)) {
      alert("Bu Excel sütunu zaten eşlenmiş!");
      return;
    }

    const newMappings = [
      ...mappings,
      { excel_column: selectedExcel, notion_property: selectedNotion },
    ];
    setMappings(newMappings);
    onChange(newMappings);
    setSelectedExcel("");
    setSelectedNotion("");
  };

  const removeMapping = (index) => {
    const newMappings = mappings.filter((_, i) => i !== index);
    setMappings(newMappings);
    onChange(newMappings);
  };

  return (
    <div className="p-4 border rounded-xl shadow-md bg-white">
      <h3 className="text-lg font-semibold mb-2">Excel → Notion Eşleme</h3>

      <div className="flex gap-2 mb-3">
        <select
          className="border p-2 rounded-md flex-1"
          value={selectedExcel}
          onChange={(e) => setSelectedExcel(e.target.value)}
        >
          <option value="">Excel sütunu seç...</option>
          {excelColumns.map((col) => (
            <option key={col} value={col}>
              {col}
            </option>
          ))}
        </select>

        <select
          className="border p-2 rounded-md flex-1"
          value={selectedNotion}
          onChange={(e) => setSelectedNotion(e.target.value)}
        >
          <option value="">Notion property seç...</option>
          {notionProperties.map((prop) => (
            <option key={prop.name} value={prop.name}>
              {prop.name} ({prop.type})
            </option>
          ))}
        </select>

        <button
          onClick={addMapping}
          className="bg-blue-500 text-white px-4 py-2 rounded-md"
        >
          Ekle
        </button>
      </div>

      <ul className="space-y-2">
        {mappings.map((m, index) => (
          <li
            key={index}
            className="flex justify-between items-center border p-2 rounded-md"
          >
            <span>
              {m.excel_column} → {m.notion_property}
            </span>
            <button
              onClick={() => removeMapping(index)}
              className="bg-red-500 text-white px-2 py-1 rounded-md"
            >
              Sil
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MappingSection;
