// AutoSyncDashboard.tsx

import React, { useState, useEffect } from 'react';
import './Dashboard.css'; // Yeni stil dosyamız
import { formatDistanceToNow, parseISO } from 'date-fns';
import { tr } from 'date-fns/locale';

const API_BASE_URL = 'http://127.0.0.1:8000';

type AutoSyncConfig = {
    id: number;
    user_id: number;
    excel_file_id: string;
    notion_database_id: string;
    direction: 'excel-to-notion' | 'notion-to-excel';
    interval_minutes: number;
    is_active: boolean;
    last_synced: string | null;
    created_at: string;
    // Toplam eşleşme sayısını backend'den aldığımızı varsayıyoruz
    mapping_count: number; 
};


type MappingDetail = {
    id: number;
    excel_column: string;
    notion_property: string;
    data_type: string;
    // Backend'den gelen yeni alanları ekliyoruz
    user_id: number; 
    is_auto_sync_enabled: boolean;
};


function AutoSyncDashboard() {
    const [configs, setConfigs] = useState<AutoSyncConfig[]>([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState('');
    const userId = localStorage.getItem('user_id');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentMappings, setCurrentMappings] = useState<MappingDetail[]>([]);
    const [currentConfigName, setCurrentConfigName] = useState('');
    const [modalLoading, setModalLoading] = useState(false);
    const [currentConfigId, setCurrentConfigId] = useState<number | null>(null); // YENİ: Hangi config'in açıldığını tutar

    

    useEffect(() => {
        if (userId) {
            fetchConfigs(userId);
        } else {
            setMessage('Kullanıcı kimliği bulunamadı. Lütfen giriş yapın.');
            setLoading(false);
        }
    }, [userId]);

    const fetchConfigs = async (currentUserId: string) => {
    setLoading(true);
    try {
        // Backend'in beklediği format: /autosync/list/userID
        const res = await fetch(`${API_BASE_URL}/autosync/list/${currentUserId}`); 
        
        const data = await res.json();
        
        if (res.ok) {
            // KRİTİK DÜZELTME: Backend doğrudan bir liste (Array) döndürdüğü için, 
            // data objesini doğrudan kullanıyoruz. 
            // Eğer veri gelmezse boş liste (data || []) kullanırız.
            setConfigs(data || []); 
            // Hata mesajını temizleyelim
            setMessage('');
        } else {
            // Hata yönetimi, Backend'in detay mesajını gösterir
            setMessage(`Ayarlar alınamadı: ${data.detail || data.error || 'Bilinmeyen Hata'}`);
            setConfigs([]); // Hata durumunda listeyi temizle
        }
    } catch {
        setMessage('Ayarları getirirken ağ hatası oluştu.');
        setConfigs([]);
    } finally {
        setLoading(false);
    }
};

    const fetchMappingDetails = async (configId: number, configDirection: 'excel-to-notion' | 'notion-to-excel') => {
        setModalLoading(true);
        setIsModalOpen(true);
        setCurrentConfigName(configDirection === 'excel-to-notion' ? 'Excel → Notion' : 'Notion → Excel');
        setCurrentConfigId(configId); // YENİ: Config ID'yi state'e kaydet
        setMessage('');

        try {
            const res = await fetch(`${API_BASE_URL}/autosync/mappings/${configId}`);
            
            // DÜZELTME: Yanıtı önce genel bir tipte yakalıyoruz
            const data: any = await res.json(); 
            console.log("Mapping Detayları (API Yanıtı):", data);
            if (res.ok) {
                // Başarılı: Veri bir MappingDetail[] listesi olmalı
                setCurrentMappings(data as MappingDetail[]);
            } else {
                // Başarısız: Veri bir hata objesi olmalı ({ detail: string } veya { error: string })
                const errorMessage = data.detail || data.error || 'Bilinmeyen Hata';
                setMessage(`Eşleşme detayları alınamadı: ${errorMessage}`);
                setCurrentMappings([]);
            }
        } catch {
            setMessage('Eşleşme detaylarını alırken ağ hatası oluştu.');
            setCurrentMappings([]);
        } finally {
            setModalLoading(false);
        }
    };

    const renderMappingModal = () => {
        if (!isModalOpen) return null;

        return (
            <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
                <div className="modal-content" onClick={e => e.stopPropagation()}>
                    <h3>{currentConfigName} Eşleşme Detayları</h3>
                    {modalLoading ? (
                        <p>Detaylar yükleniyor...</p>
                    ) : currentMappings.length > 0 ? (
                        <table className="modal-table">
                            <thead>
                                <tr>
                                    <th>Excel Sütunu</th>
                                    <th>Notion Özelliği</th>
                                    <th>Aksiyonlar</th> {/* Notion Tipi yerine Aksiyonlar */}
                                </tr>
                            </thead>
                            <tbody>
                                {currentMappings.map(m => (
                                    <tr key={m.id}>
                                        <td>{m.excel_column || '---'}</td> 
                                        <td>{m.notion_property || '---'}</td>
                                        {/* YENİ AKSİYONLAR SÜTUNU */}
                                        <td className="action-cell">
                                            {/* Sync butonu BURADAN KALDIRILDI */}
                                            <button 
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    handleRemoveAutoSync(m.id, currentConfigId!); 
                                                }}
                                                className="action-btn delete"
                                                title="Bu eşleşmeyi otomatik senkronizasyondan kaldır"
                                            >
                                                Kaldır
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>Bu işte kayıtlı otomatik senkronizasyon eşleşmesi bulunmamaktadır.</p>
                    )}
                    <button className="primary-btn close-btn" onClick={() => setIsModalOpen(false)}>Kapat</button>
                </div>
            </div>
        );
    };

    
        

    const handleRemoveAutoSync = async (mappingId: number, configId: number) => {
         if (!configId) {
            setMessage('Hata: Config ID bulunamadı.');
            return;
        }
        if (!window.confirm('Bu sütun eşleşmesinin otomatik senkronizasyonunu kalıcı olarak kaldırmak istediğinizden emin misiniz?')) {
            return;
        }

        try {
            // 🎯 KRİTİK DEĞİŞİKLİK: configId'yi de URL'ye ekliyoruz
            const res = await fetch(`${API_BASE_URL}/autosync/mapping/disable/${mappingId}/${configId}`, {
                method: 'POST',
            });
            
            const data = await res.json();
            
            if (res.ok) {
                setMessage(data.message);
                // Modal'daki görünümü güncelle: Kaldırılan kaydı listeden çıkar
                setCurrentMappings(prev => prev.filter(m => m.id !== mappingId));
                // Ana Dashboard listesinin güncellenmesini tetikle (Mapping sayısı değiştiği için)
                fetchConfigs(userId!); 

            } else {
                setMessage(`Kaldırma işlemi başarısız: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Kaldırma işlemi sırasında ağ hatası oluştu.');
        }
    };

    const handleManualSync = async (mappingId: number, configId: number) => {
        setMessage('');
        try {
            const res = await fetch(`${API_BASE_URL}/autosync/mapping/manual-sync/${mappingId}/${configId}`, {
                method: 'POST',
            });
            
            const data = await res.json();
            
            if (res.ok) {
                setMessage(data.message);
            } else {
                setMessage(`Manuel Sync Başarısız: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Manuel Sync sırasında ağ hatası oluştu.');
        }
    };







    const toggleSyncStatus = async (configId: number, currentStatus: boolean) => {
        try {
            const endpoint = currentStatus ? 'deactivate' : 'activate';
            const res = await fetch(`${API_BASE_URL}/autosync/${endpoint}/${configId}`, {
                method: 'POST',
            });
            
            const data = await res.json();
            
            if (res.ok) {
                setMessage(data.message);
                // State'i güncelle
                setConfigs(prev => 
                    prev.map(c => c.id === configId ? { ...c, is_active: !currentStatus } : c)
                );
            } else {
                setMessage(`Durum güncellenemedi: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Durum güncellenirken ağ hatası oluştu.');
        }
    };

    const deleteSyncConfig = async (configId: number) => {
        if (!window.confirm('Bu otomatik senkronizasyon işini silmek istediğinizden emin misiniz?')) {
            return;
        }
        
        try {
            const res = await fetch(`${API_BASE_URL}/autosync/delete/${configId}`, {
                method: 'DELETE',
            });
            
            if (res.ok) {
                setMessage('Senkronizasyon işi başarıyla silindi.');
                // State'ten sil
                setConfigs(prev => prev.filter(c => c.id !== configId));
            } else {
                const data = await res.json();
                setMessage(`Silinemedi: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Silme işlemi sırasında ağ hatası oluştu.');
        }
    };

    const formatLastSync = (dateString: string | null) => {
        if (!dateString) return 'Hiç senkronize edilmedi';
        try {
            const date = parseISO(dateString);
            return `${formatDistanceToNow(date, { addSuffix: true, locale: tr })} önce`;
        } catch {
            return 'Geçersiz Tarih';
        }
    };
    
    // --- Render Metotları ---
    
    if (loading) {
        return <div className="dashboard-container"><p>Ayarlar yükleniyor...</p></div>;
    }

    return (
        <div className="dashboard-container">
            <h2>Otomatik Senkronizasyon Dashboard</h2>
            {message && <div className="dashboard-message">{message}</div>}

            {configs.length === 0 ? (
                <div className="empty-state">
                    <p>Aktif otomatik senkronizasyon işi bulunmamaktadır.</p>
                    <button onClick={() => window.history.back()} className="secondary-btn">Yeni Sync Oluştur</button>
                </div>
            ) : (
                <table className="sync-table">
                    <thead>
                        <tr>
                            <th>Yön</th>
                            <th>Kaynak/Hedef</th>
                            <th>Sıklık</th>
                            <th>Eşleşme Sayısı</th>
                            <th>Son Senk.</th>
                            <th>Durum</th>
                            <th>Aksiyon</th>
                        </tr>
                    </thead>
                    <tbody>
                        {configs.map(config => (
                            <tr key={config.id} className={config.is_active ? 'active-row' : 'inactive-row'} onClick={() =>  fetchMappingDetails(config.id, config.direction)}
                                title="Eşleşme detaylarını görmek için tıklayın"
                            >
                                <td>
                                    {config.direction === 'excel-to-notion' 
                                        ? 'Excel → Notion' 
                                        : 'Notion → Excel'}
                                </td>
                                <td>
                                    {/* KRİTİK DÜZELTME: ID'lerin varlığını kontrol et */}
                                    {config.direction === 'excel-to-notion' 
                                        ? `Excel (ID: ${config.excel_file_id 
                                            ? config.excel_file_id.substring(0, 8) + '...'
                                            : 'N/A'})` // ID yoksa N/A göster
                                        : `Notion (ID: ${config.notion_database_id 
                                            ? config.notion_database_id.substring(0, 8) + '...'
                                            : 'N/A'})`}
                                </td>
                                <td>Her {config.interval_minutes} Dakika</td>
                                <td>{config.mapping_count} Sütun</td>
                                <td>{formatLastSync(config.last_synced)}</td>
                                <td>
                                    <span className={`status-badge ${config.is_active ? 'active' : 'inactive'}`}>
                                        {config.is_active ? 'AKTİF' : 'DURDURULDU'}
                                    </span>
                                </td>
                                <td className="action-cell">
                                    <button 
                                        onClick={() => toggleSyncStatus(config.id, config.is_active)}
                                        className={`action-btn ${config.is_active ? 'deactivate' : 'activate'}`}
                                        title={config.is_active ? 'Durdur' : 'Başlat'}
                                    >
                                        {config.is_active ? 'Durdur' : 'Başlat'}
                                    </button>
                                    <button 
                                        onClick={() => deleteSyncConfig(config.id)}
                                        className="action-btn delete"
                                        title="Sil"
                                    >
                                        Sil
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            
            {/* Yenile butonu */}
            <button onClick={() => fetchConfigs(userId!)} className="secondary-btn refresh-btn">
                Listeyi Yenile
            </button>
            {renderMappingModal()} 
        </div>
    );
}

export default AutoSyncDashboard;
