import React from 'react';

const API_BASE_URL = 'http://127.0.0.1:8000'; 

// KRÄ°TÄ°K: setStep tipi geniÅŸletildi ve userEmail eklendi.
interface HeaderProps {
    // TÃ¼m navigasyon adÄ±mlarÄ±nÄ± iÃ§eriyor
    setStep: (step: 'home' | 'privacy' | 'terms' | 'connect' | 'dashboard' | 'profile' | 'pricing') => void; 
    userEmail: string | null; // App.tsx'ten gelen kullanÄ±cÄ± e-postasÄ±
}

const HeaderComponent: React.FC<HeaderProps> = ({ setStep, userEmail }) => {
    
    // KullanÄ±cÄ±nÄ±n giriÅŸ yapÄ±p yapmadÄ±ÄŸÄ±nÄ± kontrol eder
    const loggedIn = !!userEmail; 
    
    return (
        <header className="header">
            <div className="container">
                {/* Logo: TÄ±klanÄ±nca ana sayfaya yÃ¶nlendir */}
                <div className="logo" onClick={() => setStep('home')}>NotiXel</div> 
                
                <nav className="nav">
                    {/* Ana sayfadaki anchor linklere yÃ¶nlendirme */}
                    <a href="#features">Features</a>
                    <a href="#pricing" onClick={(e) => { e.preventDefault(); setStep('pricing'); }}>Pricing</a>
                    {/* Dashboard ve Yeni Sync butonlarÄ± Auth butonlarÄ±na taÅŸÄ±ndÄ±ÄŸÄ± iÃ§in buradan kaldÄ±rÄ±ldÄ±. */}
                    <a href="#faq">FAQ</a>
                </nav>

                <div className="auth-buttons">
                    {/* --- KULLANICI GÄ°RÄ°Åž YAPMIÅžSA (LOGGED IN) --- */}
                    {loggedIn ? (
                        <>
                            {/* ðŸš¨ YENÄ° BUTON: Dashboard */}
                            <button 
                                onClick={() => setStep('dashboard')}
                                className="btn btn-secondary" // veya istediÄŸiniz bir sÄ±nÄ±f
                                title="Senkronizasyon Ä°ÅŸlemlerini YÃ¶net"
                                style={{ marginRight: '10px' }} // Butonlar arasÄ±na boÅŸluk ekle
                            >
                                Dashboard
                            </button>

                            {/* ðŸš¨ YENÄ° BUTON: Yeni Sync OluÅŸtur */}
                            <button 
                                onClick={() => setStep('connect')} 
                                className="btn btn-primary"
                                title="Yeni Excel ve Notion BaÄŸlantÄ±sÄ± Kur"
                                style={{ marginRight: '10px' }} // Butonlar arasÄ±na boÅŸluk ekle
                            >
                                âž• Yeni Sync
                            </button>
                            
                            {/* ðŸš¨ PROFÄ°L BUTONU (E-posta ile) - En saÄŸda yer alacak */}
                            <button
                                onClick={() => setStep('profile')}
                                className="btn btn-login"
                                title="Abonelik ve Profil YÃ¶netimi"
                                style={{ 
                                    backgroundColor: '#4CAF50', 
                                    fontWeight: 'bold',
                                    cursor: 'pointer' 
                                }}
                            >
                                {userEmail} {/* E-posta adresi buton metni olarak gÃ¶rÃ¼nÃ¼r */}
                            </button>
                            
                        </>
                    ) : (
                        /* --- KULLANICI GÄ°RÄ°Åž YAPMAMIÅžSA (LOGGED OUT) --- */
                        <a 
                            href={`${API_BASE_URL}/connect/microsoft`} 
                            className="btn btn-login"
                        >
                            Log in / Get Started
                        </a>
                    )}
                </div>
            </div>
        </header>
    );
};

export default HeaderComponent;
