import React from 'react';
import './Features.css'; // Yeni stil dosyamızı import ediyoruz

// setStep tipi, navigasyon için gerekli adımları içermelidir.
interface FeaturesProps {
    // 'connect' (giriş yapma) ve 'pricing' (fiyatlandırma) adımlarına geri dönüş için eklendi.
    setStep: (step: 'home' | 'connect' | 'pricing') => void; 
}

interface FeatureItem {
    icon: string;
    title: string;
    description: string;
    // Kategorileri belirterek gruplama yapıyoruz
    category: 'Kontrol ve Güvenlik' | 'Otomasyon ve Hız' | 'Kullanım Kolaylığı';
}

const FEATURE_DATA: FeatureItem[] = [
    // --- KONTROL VE GÜVENLİK ---
    {
        icon: '⚙️',
        title: 'Detaylı Sütun Eşleme (Granular Sync)',
        description: 'Hangi Excel sütunlarının hangi Notion özelliklerine karşılık geleceğine siz karar verin. Sadece ihtiyacınız olan veriyi senkronize edin ve geri kalanı olduğu gibi bırakın.',
        category: 'Kontrol ve Güvenlik',
    },
    {
        icon: '🔑',
        title: 'Özel Senkronizasyon ID Sistemi',
        description: 'Her senkronize edilen satır, benzersiz bir ID ile izlenir. Bu sayede satırları yeniden sıralasanız bile NotiXel, yanlış kaydı asla güncellemez.',
        category: 'Kontrol ve Güvenlik',
    },
    {
        icon: '🛡️',
        title: 'Güvenli ve Gizli Veri İşleme',
        description: 'Hassas Microsoft ve Notion API anahtarlarınız ve verileriniz, KVKK/GDPR uyumlu sunucularda şifrelenmiş olarak tutulur.',
        category: 'Kontrol ve Güvenlik',
    },
    // --- OTOMASYON VE HIZ ---
    {
        icon: '🔄',
        title: 'Gerçek İki Yönlü Senkronizasyon (Two-Way Sync)',
        description: 'Hem Excel\'den Notion\'a hem de Notion\'dan Excel\'e çift yönlü veri akışını destekler. İş akışınızın yönünü siz belirlersiniz.',
        category: 'Otomasyon ve Hız',
    },
    {
        icon: '⏰',
        title: 'Otomatik ve Planlı Senkronizasyon',
        description: 'Planınıza göre **15 dakikaya** kadar inen periyotlarda çalışan, eller serbest (set-and-forget) otomatik senkronizasyon işleri oluşturun.',
        category: 'Otomasyon ve Hız',
    },
    {
        icon: '⚡️',
        title: 'Anlık Manuel Senkronizasyon',
        description: 'Acil bir değişiklik mi gerekiyor? Yapılandırılmış senkronizasyon işinizi Dashboard\'unuzdan tek bir tıklamayla anında çalıştırın, otomatik planlamayı beklemeyin.',
        category: 'Otomasyon ve Hız',
    },
    // --- KULLANIM KOLAYLIĞI ---
    {
        icon: '🖱️',
        title: 'Sıfır Kod Gerekliliği',
        description: 'Teknik bilgiye veya karmaşık API ayarlarına gerek yok. Bağlantı, eşleme ve senkronizasyon kurulumunu sadece birkaç tıklamayla tamamlayın.',
        category: 'Kullanım Kolaylığı',
    },
    {
        icon: '🛠️',
        title: 'Hata Olmadan Otomatik Sütun Oluşturma',
        description: 'Excel\'de eşlemek istediğiniz bir sütun Notion veritabanınızda mevcut değilse, NotiXel onu sizin için doğru veri tipinde (Sayı, Tarih, Metin vb.) otomatik olarak oluşturur.',
        category: 'Kullanım Kolaylığı',
    },
    {
        icon: '📊',
        title: 'Merkezi Yönetim Dashboard\'u',
        description: 'Oluşturduğunuz tüm otomatik senkronizasyon işlerini tek bir merkezi kontrol paneli üzerinden kolayca görüntüleyin, yönetin ve durumlarını anlık olarak takip edin.',
        category: 'Kullanım Kolaylığı',
    },
];

const Features: React.FC<FeaturesProps> = ({ setStep }) => {
    
    // Feature'ları kategoriye göre grupluyoruz (Render kolaylığı için)
    const groupedFeatures = FEATURE_DATA.reduce((acc, feature) => {
        if (!acc[feature.category]) {
            acc[feature.category] = [];
        }
        acc[feature.category].push(feature);
        return acc;
    }, {} as Record<FeatureItem['category'], FeatureItem[]>);

    // Bağlantı URL'sini Home.tsx/App.tsx'ten almalısınız. Varsayılanı kullanıyoruz.
    const MICROSOFT_CONNECT_URL = 'http://127.0.0.1:8000/connect/microsoft'; 

    return (
        <div className="features-page-container container">
            <header className="features-header">
                <h1>NotiXel Özellikleri</h1>
                <p className="features-subtitle">Verilerinizi senkronize etmenin en akıllı, en hızlı ve en güvenilir yolu.</p>
            </header>

            {/* Kategori Bazlı Özellik Listesi */}
            {Object.entries(groupedFeatures).map(([category, features]) => (
                <section key={category} className="feature-category-section">
                    <h2 className="category-title">{category}</h2>
                    <div className="features-grid">
                        {features.map((feature, index) => (
                            <div key={index} className="feature-card">
                                <div className="feature-icon">{feature.icon}</div>
                                <h3>{feature.title}</h3>
                                <p>{feature.description}</p>
                            </div>
                        ))}
                    </div>
                </section>
            ))}

            {/* Fiyatlandırmaya Yönlendiren Son CTA */}
            <section className="features-cta-section">
                <h2>Hemen Başlayın ve Veri Otomasyonunda Kontrolü Ele Alın.</h2>
                <p>Karmaşık kodlara veya manuel veri girişine veda edin. Planlarımızı inceleyin veya ücretsiz denemeye başlayın.</p>
                <div className="cta-buttons">
                    <button 
                        onClick={() => setStep('pricing')} 
                        className="btn btn-primary btn-lg"
                    >
                        Fiyatlandırma Planlarını İncele →
                    </button>
                    
                </div>
            </section>
        </div>
    );
};

export default Features;
