    // 'connect' (giri≈ü yapma) ve 'pricing' (fiyatlandƒ±rma) adƒ±mlarƒ±na geri d√∂n√º≈ü i√ßin eklendi.
    setStep: (step: 'home' | 'connect' | 'pricing') => void; 
}

interface FeatureItem {
    icon: string;
    title: string;
    description: string;
    // Kategorileri belirterek gruplama yapƒ±yoruz
