import React, { useState, useEffect } from 'react';
import './App.css';

const USER_ID = 1; 
type AppStep = 'connect' | 'select' | 'mapping';

function App() {
  const [step, setStep] = useState<AppStep>('connect'); 
  const [excelFiles, setExcelFiles] = useState<{ id: string; name: string }[]>([]);
  const [excelColumns, setExcelColumns] = useState<{ name: string; type: string }[]>([]);
  const [notionProperties, setNotionProperties] = useState<{ name: string; type: string }[]>([]);
  const [selectedExcelId, setSelectedExcelId] = useState<string>('');
  const [notionDbId, setNotionDbId] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const API_BASE_URL = 'http://127.0.0.1:8000';

  // --- İyileştirilmiş API Akışı ---

  // Microsoft yetkilendirmesini ve dosya listelemeyi birleştir
  const handleMicrosoftConnect = () => {
    window.location.href = `${API_BASE_URL}/connect/microsoft`;
  };

  const handleNotionConnect = () => {
    setMessage('Notion hesabınızı bağlamak için, Notion\'da herhangi bir veritabanı veya sayfasına gidin, "..." menüsünden "Add connections"ı seçin ve oluşturduğunuz entegrasyonu ("Excel Sync") ekleyin.');
  };

  const fetchExcelFiles = async () => {
    try {
      setMessage('Excel dosyaları yükleniyor...');
      const response = await fetch(`${API_BASE_URL}/get-excel-files?user_id=${USER_ID}`);
      if (!response.ok) {
        throw new Error('Dosyalar alınamadı. Hesabınızın bağlı olduğundan emin olun.');
      }
      const data = await response.json();
      setExcelFiles(data.files);
      setStep('select');
      setMessage('');
    } catch (error) {
      if (error instanceof Error) {
        setMessage(`Hata: ${error.message}`);
      } else {
        setMessage('Bilinmeyen bir hata oluştu.');
      }
    }
  };

  // Bu hook, sayfa yüklendiğinde URL'deki 'code' parametresini kontrol eder.
  // Bu, Microsoft callback'inden dönüldüğünde çalışır.
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const authCode = params.get('code');

    // Eğer URL'de yetkilendirme kodu varsa, dosyaları otomatik olarak çek.
    if (authCode) {
      // Bu kısım, backend'de token değişimi zaten yapıldığı için doğrudan dosya listelemeye geçebilir.
      // Eğer backend'de token takası manuel tetikleniyorsa, buraya ek bir fetch isteği gerekebilir.
      // Mevcut backend'iniz token'ı otomatik kaydettiği için bu şekilde çalışacaktır.
      fetchExcelFiles();
      // URL'deki parametreleri temizle
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, []);

  // --- Diğer Fonksiyonlar aynı kalacak ---
  const handleGoToMapping = async () => {
    if (selectedExcelId && notionDbId) {
      setMessage('Eşleme verileri çekiliyor...');
      try {
        const [excelRes, notionRes] = await Promise.all([
          fetch(`${API_BASE_URL}/get-excel-columns?user_id=${USER_ID}&excel_file_id=${selectedExcelId}`),
          fetch(`${API_BASE_URL}/get-notion-properties?user_id=${USER_ID}&notion_db_id=${notionDbId}`)
        ]);

        if (!excelRes.ok || !notionRes.ok) {
          throw new Error('Eşleme verileri alınamadı.');
        }

        const excelData = await excelRes.json();
        const notionData = await notionRes.json();

        setExcelColumns(excelData.columns);
        setNotionProperties(notionData.properties);
        setStep('mapping');
        setMessage('');
      } catch (error) {
        if (error instanceof Error) {
          setMessage(`Hata: ${error.message}`);
        } else {
          setMessage('Bilinmeyen bir hata oluştu.');
        }
      }
    } else {
      setMessage('Lütfen bir Excel dosyası ve Notion veritabanı kimliği seçin.');
    }
  };

  // --- Render Fonksiyonları aynı kalacak ---
  const renderConnectStep = () => (
    <div>
      <p>Başlamak için hesaplarınızı bağlayın.</p>
      <button onClick={handleMicrosoftConnect}>Microsoft Hesabını Bağla</button>
      <button onClick={handleNotionConnect}>Notion Hesabını Bağla</button>
      <p>Microsoft bağlantısını tamamladıktan sonra, dosyalarınız otomatik olarak yüklenecektir.</p>
    </div>
  );

  const renderSelectStep = () => (
    <div>
      <h2>Excel Dosyanızı ve Notion Veritabanınızı Seçin</h2>

      <div className="file-selection-container">
        <div className="excel-files">
          <h3>Excel Dosyaları</h3>
          {excelFiles.length > 0 ? (
            <ul>
              {excelFiles.map((file) => (
                <li key={file.id}>
                  <label>
                    <input
                      type="radio"
                      name="excelFile"
                      value={file.id}
                      checked={selectedExcelId === file.id}
                      onChange={(e) => setSelectedExcelId(e.target.value)}
                    />
                    {file.name}
                  </label>
                </li>
              ))}
            </ul>
          ) : (
            <p>Excel dosyası bulunamadı. Lütfen OneDrive hesabınızda .xlsx dosyası olduğundan emin olun.</p>
          )}
        </div>

        <div className="notion-db">
          <h3>Notion Veritabanı ID</h3>
          <p>Notion veritabanı ID'sini URL'den kopyalayıp yapıştırın.</p>
          <input
            type="text"
            placeholder="Notion Veritabanı ID'sini girin..."
            value={notionDbId}
            onChange={(e) => setNotionDbId(e.target.value)}
          />
        </div>
      </div>

      <button onClick={handleGoToMapping}>Eşlemeye Geç</button>
    </div>
  );

  const renderMappingStep = () => (
    <div>
      <h2>Sütunları Eşleştirin</h2>
      <div className="mapping-container">
        <div className="excel-side">
          <h3>Excel Sütunları</h3>
          <ul>
            {excelColumns.map((col) => (
              <li key={col.name}>{col.name}</li>
            ))}
          </ul>
        </div>
        <div className="notion-side">
          <h3>Notion Özellikleri</h3>
          <ul>
            {notionProperties.map((prop) => (
              <li key={prop.name}>{prop.name}</li>
            ))}
          </ul>
        </div>
      </div>
      <button onClick={() => setMessage('Eşleme kaydediliyor...')}>Eşlemeyi Kaydet</button>
    </div>
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>Notion & Excel Senkronizasyonu</h1>

        {message && <p className="message">{message}</p>}

        {step === 'connect' && renderConnectStep()}
        {step === 'select' && renderSelectStep()}
        {step === 'mapping' && renderMappingStep()}
      </header>
    </div>
  );
}

export default App;
