// AutoSyncDashboard.tsx

import React, { useState, useEffect } from 'react';
import './Dashboard.css'; // Yeni stil dosyamÄ±z
import { formatDistanceToNow, parseISO } from 'date-fns';
import { tr } from 'date-fns/locale';

const API_BASE_URL = 'http://127.0.0.1:8000';

type AutoSyncConfig = {
    id: number;
    user_id: number;
    excel_file_id: string;
    notion_database_id: string;
    direction: 'excel-to-notion' | 'notion-to-excel';
    interval_minutes: number;
    is_active: boolean;
    last_synced: string | null;
    created_at: string;
    // Toplam eÅŸleÅŸme sayÄ±sÄ±nÄ± backend'den aldÄ±ÄŸÄ±mÄ±zÄ± varsayÄ±yoruz
    mapping_count: number; 
};


type MappingDetail = {
    id: number;
    excel_column: string;
    notion_property: string;
    data_type: string;
    // Backend'den gelen yeni alanlarÄ± ekliyoruz
    user_id: number; 
    is_auto_sync_enabled: boolean;
};


function AutoSyncDashboard() {
    const [configs, setConfigs] = useState<AutoSyncConfig[]>([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState('');
    const userId = localStorage.getItem('user_id');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentMappings, setCurrentMappings] = useState<MappingDetail[]>([]);
    const [currentConfigName, setCurrentConfigName] = useState('');
    const [modalLoading, setModalLoading] = useState(false);
    const [currentConfigId, setCurrentConfigId] = useState<number | null>(null); // YENÄ°: Hangi config'in aÃ§Ä±ldÄ±ÄŸÄ±nÄ± tutar

    

    useEffect(() => {
        if (userId) {
            fetchConfigs(userId);
        } else {
            setMessage('KullanÄ±cÄ± kimliÄŸi bulunamadÄ±. LÃ¼tfen giriÅŸ yapÄ±n.');
            setLoading(false);
        }
    }, [userId]);

    const fetchConfigs = async (currentUserId: string) => {
    setLoading(true);
    try {
        // Backend'in beklediÄŸi format: /autosync/list/userID
        const res = await fetch(`${API_BASE_URL}/autosync/list/${currentUserId}`); 
        
        const data = await res.json();
        
        if (res.ok) {
            // KRÄ°TÄ°K DÃœZELTME: Backend doÄŸrudan bir liste (Array) dÃ¶ndÃ¼rdÃ¼ÄŸÃ¼ iÃ§in, 
            // data objesini doÄŸrudan kullanÄ±yoruz. 
            // EÄŸer veri gelmezse boÅŸ liste (data || []) kullanÄ±rÄ±z.
            setConfigs(data || []); 
            // Hata mesajÄ±nÄ± temizleyelim
            setMessage('');
        } else {
            // Hata yÃ¶netimi, Backend'in detay mesajÄ±nÄ± gÃ¶sterir
            setMessage(`Ayarlar alÄ±namadÄ±: ${data.detail || data.error || 'Bilinmeyen Hata'}`);
            setConfigs([]); // Hata durumunda listeyi temizle
        }
    } catch {
        setMessage('AyarlarÄ± getirirken aÄŸ hatasÄ± oluÅŸtu.');
