    }
};

    const fetchMappingDetails = async (configId: number, configDirection: 'excel-to-notion' | 'notion-to-excel') => {
        setModalLoading(true);
        setIsModalOpen(true);
        setCurrentConfigName(configDirection === 'excel-to-notion' ? 'Excel â†’ Notion' : 'Notion â†’ Excel');
        setCurrentConfigId(configId); // YENÄ°: Config ID'yi state'e kaydet
        setMessage('');

        try {
            const res = await fetch(`${API_BASE_URL}/autosync/mappings/${configId}`);
            
            // DÃœZELTME: YanÄ±tÄ± Ã¶nce genel bir tipte yakalÄ±yoruz
            const data: any = await res.json(); 
            console.log("Mapping DetaylarÄ± (API YanÄ±tÄ±):", data);
            if (res.ok) {
                // BaÅŸarÄ±lÄ±: Veri bir MappingDetail[] listesi olmalÄ±
                setCurrentMappings(data as MappingDetail[]);
            } else {
                // BaÅŸarÄ±sÄ±z: Veri bir hata objesi olmalÄ± ({ detail: string } veya { error: string })
                const errorMessage = data.detail || data.error || 'Bilinmeyen Hata';
                setMessage(`EÅŸleÅŸme detaylarÄ± alÄ±namadÄ±: ${errorMessage}`);
                setCurrentMappings([]);
            }
        } catch {
            setMessage('EÅŸleÅŸme detaylarÄ±nÄ± alÄ±rken aÄŸ hatasÄ± oluÅŸtu.');
            setCurrentMappings([]);
        } finally {
            setModalLoading(false);
        }
    };

    const renderMappingModal = () => {
        if (!isModalOpen) return null;

        return (
            <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
                <div className="modal-content" onClick={e => e.stopPropagation()}>
                    <h3>{currentConfigName} EÅŸleÅŸme DetaylarÄ±</h3>
                    {modalLoading ? (
                        <p>Detaylar yÃ¼kleniyor...</p>
                    ) : currentMappings.length > 0 ? (
                        <table className="modal-table">
                            <thead>
                                <tr>
                                    <th>Excel SÃ¼tunu</th>
                                    <th>Notion Ã–zelliÄŸi</th>
                                    <th>Aksiyonlar</th> {/* Notion Tipi yerine Aksiyonlar */}
                                </tr>
                            </thead>
                            <tbody>
                                {currentMappings.map(m => (
                                    <tr key={m.id}>
