                                        <td className="action-cell">
                                            <button 
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    // ðŸŽ¯ DÃœZELTME: currentConfigId state'ini kullan
                                                    handleManualSync(m.id, currentConfigId!); 
                                                }}
                                                className="action-btn activate"
                                                style={{ marginRight: '5px' }}
                                                title="Hemen bu eÅŸleÅŸmeyi senkronize et"
                                            >
                                                Sync
                                            </button>
                                            <button 
                                                onClick={(e) => {
                                                    e.stopPropagation();
                                                    // ðŸŽ¯ DÃœZELTME: currentConfigId state'ini kullan
                                                    handleRemoveAutoSync(m.id, currentConfigId!); 
                                                }}
                                                className="action-btn delete"
                                                title="Bu eÅŸleÅŸmeyi otomatik senkronizasyondan kaldÄ±r"
                                            >
                                                KaldÄ±r
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>Bu iÅŸte kayÄ±tlÄ± otomatik senkronizasyon eÅŸleÅŸmesi bulunmamaktadÄ±r.</p>
                    )}
                    <button className="primary-btn close-btn" onClick={() => setIsModalOpen(false)}>Kapat</button>
                </div>
            </div>
        );
    };

    
        

    const handleRemoveAutoSync = async (mappingId: number, configId: number) => {
         if (!configId) {
            setMessage('Hata: Config ID bulunamadÄ±.');
            return;
        }
        if (!window.confirm('Bu sÃ¼tun eÅŸleÅŸmesinin otomatik senkronizasyonunu kalÄ±cÄ± olarak kaldÄ±rmak istediÄŸinizden emin misiniz?')) {
            return;
        }

        try {
            // ðŸŽ¯ KRÄ°TÄ°K DEÄžÄ°ÅžÄ°KLÄ°K: configId'yi de URL'ye ekliyoruz
            const res = await fetch(`${API_BASE_URL}/autosync/mapping/disable/${mappingId}/${configId}`, {
                method: 'POST',
            });
            
            const data = await res.json();
            
            if (res.ok) {
                setMessage(data.message);
                // Modal'daki gÃ¶rÃ¼nÃ¼mÃ¼ gÃ¼ncelle: KaldÄ±rÄ±lan kaydÄ± listeden Ã§Ä±kar
                setCurrentMappings(prev => prev.filter(m => m.id !== mappingId));
                // Ana Dashboard listesinin gÃ¼ncellenmesini tetikle (Mapping sayÄ±sÄ± deÄŸiÅŸtiÄŸi iÃ§in)
                fetchConfigs(userId!); 

            } else {
                setMessage(`KaldÄ±rma iÅŸlemi baÅŸarÄ±sÄ±z: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('KaldÄ±rma iÅŸlemi sÄ±rasÄ±nda aÄŸ hatasÄ± oluÅŸtu.');
        }
    };

    const handleManualSync = async (mappingId: number, configId: number) => {
        setMessage('');
        try {
            const res = await fetch(`${API_BASE_URL}/autosync/mapping/manual-sync/${mappingId}/${configId}`, {
                method: 'POST',
            });
            
            const data = await res.json();
            
            if (res.ok) {
                setMessage(data.message);
            } else {
                setMessage(`Manuel Sync BaÅŸarÄ±sÄ±z: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Manuel Sync sÄ±rasÄ±nda aÄŸ hatasÄ± oluÅŸtu.');
        }
    };







    const toggleSyncStatus = async (configId: number, currentStatus: boolean) => {
        try {
            const endpoint = currentStatus ? 'deactivate' : 'activate';
            const res = await fetch(`${API_BASE_URL}/autosync/${endpoint}/${configId}`, {
                method: 'POST',
            });
            
            const data = await res.json();
            
            if (res.ok) {
                setMessage(data.message);
                // State'i gÃ¼ncelle
                setConfigs(prev => 
                    prev.map(c => c.id === configId ? { ...c, is_active: !currentStatus } : c)
                );
            } else {
                setMessage(`Durum gÃ¼ncellenemedi: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Durum gÃ¼ncellenirken aÄŸ hatasÄ± oluÅŸtu.');
        }
    };

    const deleteSyncConfig = async (configId: number) => {
        if (!window.confirm('Bu otomatik senkronizasyon iÅŸini silmek istediÄŸinizden emin misiniz?')) {
            return;
        }
        
        try {
            const res = await fetch(`${API_BASE_URL}/autosync/delete/${configId}`, {
                method: 'DELETE',
            });
            
            if (res.ok) {
                setMessage('Senkronizasyon iÅŸi baÅŸarÄ±yla silindi.');
                // State'ten sil
                setConfigs(prev => prev.filter(c => c.id !== configId));
            } else {
                const data = await res.json();
                setMessage(`Silinemedi: ${data.detail || data.error}`);
            }
        } catch {
            setMessage('Silme iÅŸlemi sÄ±rasÄ±nda aÄŸ hatasÄ± oluÅŸtu.');
        }
    };

    const formatLastSync = (dateString: string | null) => {
        if (!dateString) return 'HiÃ§ senkronize edilmedi';
        try {
            const date = parseISO(dateString);
            return `${formatDistanceToNow(date, { addSuffix: true, locale: tr })} Ã¶nce`;
        } catch {
            return 'GeÃ§ersiz Tarih';
        }
    };
    
    // --- Render MetotlarÄ± ---
    
    if (loading) {
        return <div className="dashboard-container"><p>Ayarlar yÃ¼kleniyor...</p></div>;
    }

    return (
        <div className="dashboard-container">
            <h2>Otomatik Senkronizasyon Dashboard</h2>
            {message && <div className="dashboard-message">{message}</div>}

            {configs.length === 0 ? (
                <div className="empty-state">
                    <p>Aktif otomatik senkronizasyon iÅŸi bulunmamaktadÄ±r.</p>
                    <button onClick={() => window.history.back()} className="secondary-btn">Yeni Sync OluÅŸtur</button>
                </div>
            ) : (
                <table className="sync-table">
                    <thead>
                        <tr>
                            <th>YÃ¶n</th>
                            <th>Kaynak/Hedef</th>
                            <th>SÄ±klÄ±k</th>
                            <th>EÅŸleÅŸme SayÄ±sÄ±</th>
                            <th>Son Senk.</th>
                            <th>Durum</th>
                            <th>Aksiyon</th>
                        </tr>
                    </thead>
                    <tbody>
                        {configs.map(config => (
                            <tr key={config.id} className={config.is_active ? 'active-row' : 'inactive-row'} onClick={() =>  fetchMappingDetails(config.id, config.direction)}
                                title="EÅŸleÅŸme detaylarÄ±nÄ± gÃ¶rmek iÃ§in tÄ±klayÄ±n"
                            >
                                <td>
                                    {config.direction === 'excel-to-notion' 
                                        ? 'Excel â†’ Notion' 
                                        : 'Notion â†’ Excel'}
                                </td>
                                <td>
                                    {/* KRÄ°TÄ°K DÃœZELTME: ID'lerin varlÄ±ÄŸÄ±nÄ± kontrol et */}
                                    {config.direction === 'excel-to-notion' 
                                        ? `Excel (ID: ${config.excel_file_id 
                                            ? config.excel_file_id.substring(0, 8) + '...'
                                            : 'N/A'})` // ID yoksa N/A gÃ¶ster
                                        : `Notion (ID: ${config.notion_database_id 
                                            ? config.notion_database_id.substring(0, 8) + '...'
                                            : 'N/A'})`}
                                </td>
                                <td>Her {config.interval_minutes} Dakika</td>
                                <td>{config.mapping_count} SÃ¼tun</td>
                                <td>{formatLastSync(config.last_synced)}</td>
                                <td>
                                    <span className={`status-badge ${config.is_active ? 'active' : 'inactive'}`}>
                                        {config.is_active ? 'AKTÄ°F' : 'DURDURULDU'}
                                    </span>
                                </td>
                                <td className="action-cell">
                                    <button 
                                        onClick={() => toggleSyncStatus(config.id, config.is_active)}
                                        className={`action-btn ${config.is_active ? 'deactivate' : 'activate'}`}
                                        title={config.is_active ? 'Durdur' : 'BaÅŸlat'}
                                    >
                                        {config.is_active ? 'Durdur' : 'BaÅŸlat'}
                                    </button>
                                    <button 
                                        onClick={() => deleteSyncConfig(config.id)}
