// AutoSyncDashboard.tsx

import React, { useEffect, useState } from "react";

// Backend'deki AutoSyncResponse modeline uygun tip tanımı
type AutoSyncConfig = {
  id: number;
  excel_file_id: string; // Gerçekte burada dosya adı/DB adı olmalı, şimdilik ID'yi gösteriyoruz
  notion_database_id: string;
  direction: 'excel-to-notion' | 'notion-to-excel';
  interval_minutes: number;
  is_active: boolean; // Yeni alan
  mapping_ids: number[]; // Backend'den List<int> olarak geliyor
};

const API_BASE_URL = 'http://127.0.0.1:8000';

const AutoSyncDashboard: React.FC = () => {
  const [configs, setConfigs] = useState<AutoSyncConfig[]>([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState<string>('');
  
  // App.tsx'den çekilen user_id'yi kullanmalıyız. 
  // Şimdilik localStorage'dan veya varsayılan 1'den çekelim:
  const userId = localStorage.getItem('user_id') || '1'; 

  const fetchAutoSyncs = async () => {
    setLoading(true);
    setMessage('');
    try {
      // Backend'deki /autosync/list/{user_id} endpoint'ini çağırıyoruz
      const response = await fetch(`${API_BASE_URL}/autosync/list/${userId}`);
      const data = await response.json();
      
      if (response.ok) {
        setConfigs(data);
      } else {
         setMessage(`Yapılandırmalar alınamadı: ${data.detail || 'Bilinmeyen hata'}`);
      }
    } catch (error) {
      setMessage("Auto sync yapılandırmalarını alırken ağ hatası oluştu.");
      console.error("Auto sync fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAutoSyncs();
  }, [userId]);

  const handleToggle = async (configId: number) => {
    setMessage('');
    try {
      // Backend'deki /autosync/toggle/{config_id} endpoint'ini çağırıyoruz
      const response = await fetch(`${API_BASE_URL}/autosync/toggle/${configId}`, {
        method: 'PUT'
      });
      const data = await response.json();
      
      if (response.ok) {
        setMessage(data.message);
        // Başarılı olursa listeyi yenile
        fetchAutoSyncs();
      } else {
        setMessage(`Durum güncellenemedi: ${data.detail || 'Bilinmeyen hata'}`);
      }
    } catch (error) {
      setMessage("Durum güncellenirken ağ hatası oluştu.");
    }
  };
  
  const handleDelete = async (configId: number) => {
    setMessage('');
    if (!window.confirm("Bu AutoSync yapılandırmasını kalıcı olarak silmek istediğinizden emin misiniz?")) {
        return;
    }
    
    try {
      // Backend'deki /autosync/delete/{config_id} endpoint'ini çağırıyoruz
      const response = await fetch(`${API_BASE_URL}/autosync/delete/${configId}`, {
        method: 'DELETE'
      });
      const data = await response.json();
      
      if (response.ok) {
        setMessage(data.message);
        // Başarılı olursa listeyi yenile
        fetchAutoSyncs();
      } else {
        setMessage(`Silme işlemi başarısız: ${data.detail || 'Bilinmeyen hata'}`);
      }
    } catch (error) {
      setMessage("Silme işlemi sırasında ağ hatası oluştu.");
    }
  };


  if (loading) {
    return <p className="p-4">Yükleniyor...</p>;
  }

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Otomatik Sync Dashboard</h2>
      {message && <div className="message mb-4">{message}</div>}

      {configs.length === 0 ? (
        <p>Henüz otomatik sync tanımlı değil.</p>
      ) : (
        configs.map((config) => (
          <div
            key={config.id}
            className={`p-4 border rounded mb-3 shadow-sm ${config.is_active ? 'bg-green-50 border-green-300' : 'bg-red-50 border-red-300'}`}
          >
            <p className="font-semibold text-lg">
              {config.excel_file_id} 
              <span className="mx-2">
                {config.direction === 'excel-to-notion' ? '→' : '←'}
              </span> 
              {config.notion_database_id}
            </p>
            <p className="text-sm mt-1">Durum: <b className={config.is_active ? 'text-green-600' : 'text-red-600'}>
                {config.is_active ? 'AKTİF' : 'DURAKLATILDI'}
            </b></p>
            <p className="text-sm">Yenileme Aralığı: <b>{config.interval_minutes} dakika</b></p>
            <p className="text-sm">Eşleşme Sayısı: <b>{config.mapping_ids.length} sütun</b></p>
            
            <div className="mt-3 space-x-2">
                <button
                onClick={() => handleToggle(config.id)}
                className={`px-3 py-1 rounded text-white ${config.is_active ? 'bg-orange-500 hover:bg-orange-600' : 'bg-green-500 hover:bg-green-600'}`}
                >
                {config.is_active ? 'Durdur' : 'Aktifleştir'}
                </button>
                <button
                onClick={() => handleDelete(config.id)}
                className="px-3 py-1 rounded bg-red-600 text-white hover:bg-red-700"
                >
                Sil
                </button>
            </div>

          </div>
        ))
      )}
    </div>
  );
};

export default AutoSyncDashboard;
