import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ChevronDown, ChevronUp, XSquare, ToggleRight, ToggleLeft } from 'lucide-react';

// --- TİP TANIMLARI ---
type MappingDetail = {
  id: number;
  excel_column: string;
  notion_property: string;
  is_auto_sync_enabled: boolean;
};

type AutoSyncJob = {
  id: number;
  direction: 'excel-to-notion' | 'notion-to-excel'; 
  excel_file_name: string; 
  notion_db_name: string;   
  is_active: boolean;      
  mapping_details: MappingDetail[]; 
};

const API_BASE_URL = 'http://127.0.0.1:8000'; 
const USER_ID = localStorage.getItem('user_id') || '1'; 

const AutoSyncDashboard: React.FC = () => {
  const [jobs, setJobs] = useState<AutoSyncJob[]>([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState<string>('');
  const [expandedJobId, setExpandedJobId] = useState<number | null>(null); 

  // --- API ---
  const fetchJobs = async () => {
    setLoading(true);
    setMessage('');
    try {
      const response = await axios.get<AutoSyncJob[]>(`${API_BASE_URL}/autosync/list/${USER_ID}`);
      setJobs(response.data);
    } catch (error) {
      const msg = axios.isAxiosError(error) && error.response 
        ? error.response.data.detail || 'Bilinmeyen hata' 
        : 'Auto sync yapılandırmalarını alırken ağ hatası oluştu.';
      setMessage(`Sync işleri çekilemedi: ${msg}`);
      console.error("Auto sync fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, [USER_ID]);

  const toggleMappingSync = async (mappingId: number) => {
    setMessage('');
    try {
      await axios.put(`${API_BASE_URL}/autosync/toggle-mapping/${mappingId}`);
      setMessage("Sütun senkronizasyon durumu güncellendi.");
      fetchJobs();
    } catch (error) {
      setMessage("Sütun durumu güncellenemedi.");
      console.error("Sütun durumu güncellenemedi:", error);
    }
  };

  const deleteSyncJob = async (jobId: number, jobName: string) => {
    setMessage('');
    if (!window.confirm(`"${jobName}" adlı otomatik senkronizasyon işini silmek istediğinize emin misiniz?`)) {
      return;
    }
    try {
      await axios.delete(`${API_BASE_URL}/autosync/delete/${jobId}`);
      setMessage("Otomatik Sync işi başarıyla silindi.");
      setExpandedJobId(null);
      fetchJobs();
    } catch (error) {
      setMessage("Sync işi silinemedi.");
      console.error("Sync işi silinemedi:", error);
    }
  };

  // --- Yardımcı ---
  const renderJobTitle = (job: AutoSyncJob) => {
    const directionArrow = job.direction === 'excel-to-notion' ? '→' : '←';
    const source = job.direction === 'excel-to-notion' ? job.excel_file_name : job.notion_db_name;
    const target = job.direction === 'excel-to-notion' ? job.notion_db_name : job.excel_file_name;

    return `${source} ${directionArrow} ${target}`;
  };

  // --- UI ---
  if (loading) {
    return <p className="p-4 text-gray-500">Yükleniyor...</p>;
  }

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Otomatik Senkronizasyon Yönetimi</h1>
      
      {message && <div className="p-3 mb-4 rounded bg-blue-100 text-blue-800">{message}</div>}

      {jobs.length === 0 ? (
        <p className="text-gray-500">Henüz otomatik senkronizasyon işi yok.</p>
      ) : (
        <div className="space-y-4">
          {jobs.map((job) => (
            <div 
              key={job.id} 
              className="border border-gray-200 rounded-lg shadow-sm overflow-hidden bg-white"
            >
              {/* KART BAŞLIĞI */}
              <div 
                className="flex justify-between items-center p-4 cursor-pointer hover:bg-gray-50 transition"
                onClick={() => setExpandedJobId(job.id === expandedJobId ? null : job.id)}
              >
                <span className="text-lg font-medium">{renderJobTitle(job)}</span>
                <div className="flex items-center space-x-3">
                  <button 
                    onClick={(e) => { e.stopPropagation(); deleteSyncJob(job.id, renderJobTitle(job)); }}
                    className="p-1 rounded-full text-red-600 hover:bg-red-100"
                    title="Tüm Sync İşini Sil"
                  >
                    <XSquare size={20} />
                  </button>
                  {job.id === expandedJobId ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
                </div>
              </div>
              
              {/* DETAY PANELİ */}
              {job.id === expandedJobId && (
                <div className="p-4 bg-gray-50 border-t border-gray-200 space-y-2">
                  {job.mapping_details.map((mapping) => (
                    <div 
                      key={mapping.id} 
                      className="flex justify-between items-center p-2 rounded border bg-white"
                    >
                      <span className="text-sm">
                        {mapping.excel_column} <span className="mx-2 text-gray-400">⇆</span> {mapping.notion_property}
                      </span>
                      
                      <button 
                        onClick={() => toggleMappingSync(mapping.id)}
                        className={`p-1 rounded-full transition ${mapping.is_auto_sync_enabled ? 'text-green-600 hover:bg-green-100' : 'text-gray-500 hover:bg-gray-200'}`}
                        title={mapping.is_auto_sync_enabled ? "Açık (Kapatmak için tıkla)" : "Kapalı (Açmak için tıkla)"}
                      >
                        {mapping.is_auto_sync_enabled ? <ToggleRight size={22} /> : <ToggleLeft size={22} />}
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AutoSyncDashboard;
