import React from 'react';

// API_BASE_URL'i App.tsx'ten çekilen değerle sabitliyoruz
const API_BASE_URL = 'http://127.0.0.1:8000'; 

interface HeaderProps {
    // setStep, global navigasyon için gereklidir (örneğin logo tıklanması)
    setStep: (step: 'home' | 'privacy' | 'terms' | 'connect') => void; 
}

const HeaderComponent: React.FC<HeaderProps> = ({ setStep }) => {
    return (
        // `header` class'ı global stil için kullanılacaktır
        <header className="header">
            <div className="container">
                {/* Logo: Tıklanınca ana sayfaya yönlendir */}
                <div className="logo" onClick={() => setStep('home')}>NotiXel</div> 
                <nav className="nav">
                    {/* Ana sayfadaki anchor linklere yönlendirme (sayfa yenilenmez, kullanıcıyı kaydırır) */}
                    <a href="#features">Features</a>
                    <a href="#pricing">Pricing</a>
                    <a href="#faq">FAQ</a>
                </nav>
                <div className="auth-buttons">
                    {/* Microsoft bağlantı URL'sine yönlendirme */}
                    <a 
                        href={`${API_BASE_URL}/connect/microsoft`} 
                        className="btn btn-login"
                    >
                        Log in / Get Started
                    </a>
                </div>
            </div>
        </header>
    );
};

export default HeaderComponent;
