// Pricing.tsx

import React, { useEffect } from 'react';
import './Pricing.css'; // Yeni oluşturduğumuz stil dosyasını import ediyoruz

// 🚨 KRİTİK: Bu ID'leri Lemon Squeezy panelinizdeki ilgili "Product Variant" ID'leriyle DEĞİŞTİRMELİSİNİZ.
const PLAN_VARIANTS = {
    BASIC: {
        id: 12345, // GERÇEK ID ile değiştirin
        price: "19",
        name: "Basic",
        features: [
            "1 Yönlü Senkronizasyon (Excel -> Notion)", 
            "Aylık 5 Otomatik Sync Job", 
            "15 Dakika Senkronizasyon Aralık", 
            "Özel Hesap Bağlantıları",
        ],
    },
    PRO: {
        id: 67890, // GERÇEK ID ile değiştirin
        price: "49",
        name: "Pro",
        features: [
            "Çift Yönlü Senkronizasyon (Excel <-> Notion)", 
            "Aylık 20 Otomatik Sync Job", 
            "5 Dakika Senkronizasyon Aralık", 
            "Öncelikli Müşteri Desteği",
        ],
    },
    EXCLUSIVE: {
        id: 11223, // GERÇEK ID ile değiştirin
        price: "99",
        name: "Exclusive",
        features: [
            "Çift Yönlü Senkronizasyon (Excel <-> Notion)", 
            "Sınırsız Otomatik Sync Job", 
            "1 Dakika Senkronizasyon Aralık", 
            "Özel Entegrasyon Geliştirme (Talep Üzerine)",
        ],
    },
};

// Yönlendirme tipleri (Abone olduktan sonra veya giriş yapılmadığında)
interface PricingProps {
    setStep: (step: 'select' | 'home' | 'profile' | 'dashboard') => void;
}

// Lemon Squeezy'nin global olarak yüklendiği varsayılır (index.html'e eklenen script ile)
declare global {
    interface Window {
        LemonSqueezy: any; 
    }
}

const Pricing: React.FC<PricingProps> = ({ setStep }) => {
    const userId = localStorage.getItem('user_id'); 
    const LEMONSQUEEZY_STORE_URL = 'https://notixel.lemonsqueezy.com'; // 🚨 KENDİ MAĞAZA URL'NİZİ GİRİN

    useEffect(() => {
        if (!window.LemonSqueezy) {
            console.warn("Lemon Squeezy scripti henüz yüklenmedi. Lütfen public/index.html dosyasını kontrol edin.");
        }
    }, []);

    // Ödeme penceresini açan ana fonksiyon
    const handleCheckout = (variantId: number, planName: string) => {
        if (!userId) {
            alert("Lütfen önce giriş yapın. Giriş yap butonuna yönlendiriliyorsunuz.");
            setStep('home'); 
            return;
        }

        window.LemonSqueezy.Url.Open(`${LEMONSQUEEZY_STORE_URL}/checkout/buy/variant/${variantId}`, {
            embed: 1, // Pencereyi overlay olarak aç
            // custom: Bu veri, backend'deki webhook'a gönderilir.
            custom: {
                user_id: userId, // KRİTİK: Backend'in hangi kullanıcıyı güncelleyeceğini belirler
                plan_name: planName,
            },
        });
        
        // Kullanıcı ödeme penceresini kapattıktan sonra da select adımına geçmelidir
        // Bu, ödeme başarılı olsa da olmasa da akışı devam ettirmek için yapılır. 
        // Gerçek abonelik kontrolü, bir sonraki adımda (select/dashboard) backend'den çekilecektir.
        // setStep('select'); // Bunu burada yapmıyoruz, ödeme sonrası manuel yönlendirme yapılabilir.
    };
    
    // Kullanıcı giriş yapmamışsa, buton metni 'Giriş Yap' olabilir
    const ctaText = userId ? 'Şimdi Abone Ol' : 'Giriş Yap ve Abone Ol';

    return (
        <div className="pricing-page-container container">
            <header className="pricing-header">
                <h1>Hangi NotiXel Planı Size Uygun?</h1>
                <p>Veri senkronizasyonunuzun gücünü seçin. Tüm planlarımız garantili ve otomatik senkronizasyon sunar.</p>
            </header>

            <section className="pricing-cards-section">
                <div className="pricing-grid">
                    {Object.values(PLAN_VARIANTS).map((plan) => (
                        // Plan adına göre farklı bir renk sınıfı atıyoruz
                        <div key={plan.name} className={`pricing-card ${plan.name.toLowerCase()}`}>
                            <div>
                                <h2>{plan.name}</h2>
                                <p className="price">
                                    ${plan.price}<span>/ay</span>
                                </p>
                                
                                <ul className="feature-list">
                                    {plan.features.map((feature, index) => (
                                        <li key={index}>✅ {feature}</li>
                                    ))}
                                </ul>
                            </div>
                            
                            <button 
                                onClick={() => userId ? handleCheckout(plan.id, plan.name) : setStep('home')}
                                className="btn btn-primary cta-btn"
                            >
                                {plan.name === "Pro" ? "En Popüler: " : ""}
                                {ctaText}
                            </button>
                        </div>
                    ))}
                </div>
            </section>
            
            <section className="faq-prompt">
                 <h2>Abonelik Yönetimi</h2>
                 <p>Planınızı profil sayfanızdan dilediğiniz zaman yükseltebilir, düşürebilir veya iptal edebilirsiniz. İptal durumunda, ödenen dönemin sonuna kadar hizmetiniz aktif kalır.</p>
                 <p>Daha fazla sorunuz varsa, lütfen <a href="#" onClick={(e) => {e.preventDefault(); setStep('home');}}>SSS bölümümüze</a> göz atın veya <a href="mailto:support@notixel.com">destek</a> ekibimize ulaşın.</p>
            </section>
        </div>
    );
};

export default Pricing;
