// Pricing.tsx

import React, { useEffect } from 'react';
// import './Pricing.css'; // Stil dosyasını oluşturmayı unutmayın!

// 🚨 KRİTİK: Bu ID'leri Lemon Squeezy panelinizdeki ilgili "Product Variant" ID'leriyle DEĞİŞTİRMELİSİNİZ.
const PLAN_VARIANTS = {
    BASIC: {
        id: 12345, // GERÇEK ID ile değiştirin
        price: "19",
        name: "Basic",
        features: ["1 Yönlü Senkronizasyon", "Aylık 5 Sync Job", "15 Dakika Aralık", "Standart Destek"],
    },
    PRO: {
        id: 67890, // GERÇEK ID ile değiştirin
        price: "49",
        name: "Pro",
        features: ["Çift Yönlü Senkronizasyon", "Aylık 20 Sync Job", "5 Dakika Aralık", "Öncelikli Destek", "Gelişmiş Raporlama"],
    },
    EXCLUSIVE: {
        id: 11223, // GERÇEK ID ile değiştirin
        price: "99",
        name: "Exclusive",
        features: ["Çift Yönlü Senkronizasyon", "Sınırsız Sync Job", "1 Dakika Aralık", "Özel Entegrasyonlar", "Özel Müşteri Yöneticisi"],
    },
};

// Pricing sayfasından nereye döneceğimizi belirten tip
interface PricingProps {
    setStep: (step: 'select' | 'home' | 'profile' | 'dashboard') => void;
}

// Lemon Squeezy'nin global olarak yüklendiği varsayılır (index.html'e eklenen script ile)
declare global {
    interface Window {
        LemonSqueezy: any; // LemonSqueezy nesnesine erişim için
    }
}

const Pricing: React.FC<PricingProps> = ({ setStep }) => {
    // Kullanıcının ID'si. Bu ID, ödeme sonrası Backend'e gönderilecek KRİTİK bilgidir.
    const userId = localStorage.getItem('user_id'); 
    
    // 🚨 KRİTİK: Lemon Squeezy Store URL'nizi buraya girin. 
    // Örn: https://yourstore.lemonsqueezy.com
    const LEMONSQUEEZY_STORE_URL = 'https://notixel.lemonsqueezy.com'; 

    useEffect(() => {
        // index.html'e script eklendiğinden emin olmak için uyarı
        if (!window.LemonSqueezy) {
            console.warn("Lemon Squeezy scripti henüz yüklenmedi. Lütfen public/index.html dosyasını kontrol edin.");
        }
    }, []);

    // Ödeme penceresini açan ana fonksiyon
    const handleCheckout = (variantId: number, planName: string) => {
        if (!userId) {
            alert("Lütfen önce giriş yapın. Giriş yap butonuna yönlendiriliyorsunuz.");
            setStep('home'); 
            return;
        }

        // Lemon Squeezy'nin ödeme penceresini açar
        window.LemonSqueezy.Url.Open(`${LEMONSQUEEZY_STORE_URL}/checkout/buy/variant/${variantId}`, {
            embed: 1, // Pencereyi overlay olarak aç
            // custom: Bu veri, backend'deki webhook'a gönderilir.
            custom: {
                user_id: userId,
                plan_name: planName,
            },
            // Ödeme başarılı olsa bile yönlendirme yapmayacağız, 
            // kullanıcı sayfayı yenilediğinde/giriş yaptığında Backend'den gelen yeni abonelik durumunu görecek.
        });
        
        // Ödeme penceresi açıldıktan sonra, kullanıcının işlemi tamamlamasını bekliyoruz.
        console.log(`Plan: ${planName} için ödeme penceresi açıldı. User ID: ${userId}`);
    };

    if (!userId) {
         // Eğer kullanıcı giriş yapmadıysa, sadece fiyatlandırma görünmeli ve butonda "Giriş Yap" yazmalıdır.
         // Ancak sizin akışınızda zaten Pricing adımına gelmeden önce giriş yapılması gerekiyor.
         // Yine de sağlamlık için bu kontrolü tutalım.
         // Bu senaryo, kullanıcının doğrudan /pricing URL'sine gelmesi durumunda gerçekleşebilir.
    }
    
    return (
        <div className="pricing-page-container container">
            <header className="pricing-header">
                <h1>Hangi NotiXel Planı Size Uygun?</h1>
                <p>Ücretsiz plan yok. Sadece kesintisiz, otomatik ve güvenilir senkronizasyon için ihtiyacınız olan gücü seçin.</p>
            </header>

            <section className="pricing-cards-section">
                <div className="pricing-grid">
                    {Object.values(PLAN_VARIANTS).map((plan) => (
                        <div key={plan.name} className={`pricing-card ${plan.name.toLowerCase()}`}>
                            <h2>{plan.name}</h2>
                            <p className="price">
                                ${plan.price}<span>/ay</span>
                            </p>
                            
                            <ul className="feature-list">
                                {plan.features.map((feature, index) => (
                                    <li key={index}>✅ {feature}</li>
                                ))}
                            </ul>
                            
                            <button 
                                onClick={() => handleCheckout(plan.id, plan.name)}
                                className="btn btn-primary cta-btn"
                                // Kullanıcının e-posta adresini Lemon Squeezy'ye gönderebiliriz, ancak bu otomatik yapılır.
                                // 'data-config-id' veya 'data-url' kullanarak butonu direkt bağlamak da mümkündür.
                            >
                                Hemen Abone Ol
                            </button>
                        </div>
                    ))}
                </div>
            </section>
            
            <section className="faq-prompt">
                 <h2>Aboneliğinizi Dilediğiniz Zaman İptal Edin</h2>
                 <p>Tüm planlar aylıktır ve aboneliğinizi profil sayfanızdan kolayca yönetebilirsiniz.</p>
            </section>
        </div>
    );
};

export default Pricing;
